import React from 'react'
import { Grid } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles';
import m from "../images/category/men11.jpg";
import w from "../images/category/women11.jpg";
import { Link } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
    GridContainer: {
        display: "flex",
        justifyContent:'center',   
        alignItems:'center',
    },
    GridItem: {
        margin:"100px",    
        display: "flex",
        justifyContent:'center',   
        alignItems:'center', 
    },
    title: {
        fontSize:"3.2rem",
        fontWeight:'bold',
        position: 'relative',
        margin: '50px auto',    
        backgroundColor:'green',
        textAlign: 'center',
    },
    hr: { 
            backgroundColor: 'black', height: 2, flex: 1, alignSelf: 'center'


    },
    overlay: {
        top: 0,
        left: 0,
        position: 'absolute',
        width: '100%',
        height: '100%',
        background : 'rgba(0, 0, 0, 0.2)',    
        display:"flex",
        justifyContent:"center",
        alignItems:"center",
        '&:hover': {
                animation: "$lefttoright 1s forwards",
            }
      },
    center : {
        background: "rgba(255,255,255,.5)",
        color:theme.palette.fontColor.primary,
        width:"200px",
        height:"150px",
        display:"flex",
        justifyContent:"center",
        flexDirection:"column",
        alignItems:"center",
        ['@media (max-width:780px)']: {
            width:"150px",
            height:"100px",
         }
    },
    button: {    
        transition: "all 0.3s ease 0s",
        color:theme.palette.fontColor.primary,
        position: "relative",
        cursor: "pointer",
        "&:after" :{  
            backgroundColor: 'black', height: 2, flex: 1, alignSelf: 'center'

         },
        "&:hover:after":{ 
                width: "50%",
                left: "0"
            },
        },
        "@keyframes lefttoright": {
            from: { background:'rgba(0, 0, 0, 0.2)' },
            to: { background: "none" }
              }

}))

const TitleSection = (props) => {
    const classes = useStyles();
    return (            
             <Grid  item xs={12} sm={12} className={classes.GridItem}>
                <div className={classes.GridItem} />
                 <h1 className={classes.title}>{props.title}</h1>
             </Grid> 

    )
}


export default TitleSection
