import React from 'react';
import { Link } from 'react-router-dom';
import { Grid,Button, Typography,Box,Container} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { LocationOn } from '@material-ui/icons';
import Carousel from 'react-material-ui-carousel'
import i from '../images/promotion/chapeau.jpg'

const useStyles = makeStyles((theme) => ({
    PromotionContainer: {
        backgroundColor: 'white',
        display: "flex",
        justifyContent:'center',   
        alignItems:'center',
    },
    PromotionItem: {
        backgroundColor: 'white',
    },
    carousel: {
        width: '100%',
        height: '100%', 
    },
    containerItem: {
        position: 'relative', 
        minWidth:'200px',
        minHeight:'200px',
        height: '350px',
    },
    imageItem: {
        minWidth:'200px',
        minHeight:'200px',
        height: '350px',
        objectFit:"cover",     
    },
    overlay: {

      },
    priceItem: {
        fontSize: '20px',
        color: 'white',
        textDecoration: 'none'
    },
    buttonItem: {    
        transition: "all 0.3s ease 0s",
        color:theme.palette.fontColor.primary,
        position: "relative",
        cursor: "pointer",
        "&:after" :{  
        position: 'absolute',
        content: '""',
        display:"block",
        left: 0,
        top: "20px",
        right: "50%",
        bottom: "0",
        width: "0px",
        height: "2px",
        background: theme.palette.fontColor.primary,
        transition:" 0.3s ease 0s, left 0.3s ease 0s",
         },
        "&:hover:after":{ 
                width: "50%",
                left: "0"
            },
        },
}));

const items = [
    {
        price: '300',
        imageUrl: '/static/promotion/chapeau.jpg',
    },
    {
        price: '400',
        imageUrl: '/static/promotion/chapeau.jpg',
    },
    {
        price: '500',
        imageUrl: '/static/promotion/chapeau.jpg',
    },
    {
        price: '600',
        imageUrl: '/static/promotion/chapeau.jpg',
    }
];

const Item = (props) => {
  const classes = useStyles();
  return (
      <Box className={classes.containerItem}>
        <img alt="kk" src={props.item.imageUrl} className={classes.imageItem}/>
        <div className={classes.overlay}>
          <h1 className={classes.priceItem}>{props.item.price}</h1>
          <Link to="/shop" className={classes.buttonItem}>
            <h1>Shop Now</h1>
          </Link>
        </div>
      </Box> 
    );
}

export default function PromotionSection() {
  const classes = useStyles();
  return (
    <Container>
    <Grid xs={12} container className={classes.PromotionContainer}>
      <Grid item xs={12} md={6}className={classes.PromotionItem}>
        <Carousel className={classes.carousel}>
          {
            items.map( (item, i) => <Item key={i} item={item} /> )
          }
        </Carousel>
      </Grid>
      <Grid item xs={12} md={6} className={classes.PromotionItem}>
        <Typography className={classes.TitlePromotion}>Promotion</Typography>
      </Grid>
    </Grid>
    </Container>
  );
}
