import React,{useState, useEffect} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import KeyboardDoubleArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import { KeyboardArrowUp } from '@material-ui/icons';

const useStyles = makeStyles((theme) => ({
  scrollTopButton : {
    position: 'fixed',
    bottom: 40,
    right: 25,
    display: "flex", 
    justifyContent: 'center',
    alignItems: 'center',      
    borderRadius: '50%',
    width:'50px',
    height:'50px',
    backgroundColor:'black',
    color:'white',
    outline: 'none',
    border: 0,
    cursor: 'pointer',
    zIndex: 20,
    transition: ''
  },
    icon: { 
    fontSize: 35,    
    color: "white",

    "&:hover":{
        color: "green",
      },
    },

}))
const ScrollToTop = () => {
  const classes = useStyles();
  const [showTopBtn, setShowTopBtn] = useState(false);

  const goToTop = () => {
    window.scrollTo({
        top: 0,
        behavior: 'smooth',
    });
  };

  useEffect(() => {
    window.addEventListener('scroll', () => {
        if (window.scrollY > 400) {
            setShowTopBtn(true);
        } else {
            setShowTopBtn(false);
        }
    });
}, []);
  return (
    <>
    {" "}
    {showTopBtn && (
    <button onClick={goToTop} className={classes.scrollTopButton}>
      <KeyboardArrowUp className={classes.icon} />
    </button>
    )}
    {" "}
    </>
  )
}

export default ScrollToTop
