      if(pathnames[i].path === pathname){
       l = pathnames[i].id
      }
    }
  }


  const pp = 0

   pp = pathFuncion()

  return (
    <Grid item xs={12} className="headerTitlecontainer">   
      <h1>{pp}</h1>
      <div  className="menu">
      <AppBreadcrumbs />
      </div>
    </Grid>
  )
}

export default HeaderTitle;

