import React,{useState, useEffect} from 'react';
import { Grid,Button, Typography,Box,Container} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import {useNavigate,useParams,Link} from 'react-router-dom';
import axios from "axios";
import Product from '../components/Product/Product';
import Pagination from '../components/Pagination'
import w from '../images/category/men11.jpg'
const GenderMenu =[
  {
    id: "a",
    title: "All"
  },
  {
    id: "m",
    title: "Men",
    category : "men's clothing"
  },
  {
    id: "w",
    title: "Woman",
    category : "women's clothing"
  },
  {
    id: "k",
    title: "Kids",
  },
  {
    id: "j",
    title: "Jewelry",
    category : "jewelery"
  },
];

const ColorMenu =[
  {
    id: "#fdd4ce",
    title: "#fdd4ce",
  },
  {
    id: "black",
    title: "black",
  },
  {
    id: "grey",
    title: "grey",
  },
  {
    id: "pink",
    title: "pink",
  },
  {
    id: "#c0809c",
    title: "#c0809c",
  },
  {
    id: "#db8f8d",
    title: "#db8f8d",
  },
  {
    id: "lightgray",
    title: "lightgray",
  },
];

const useStyles = makeStyles((theme) => ({
    ProductDetailContainer: {
      backgroundColor: 'rgb(255, 255, 255)'
    },
    ProductDetailHeader: {
      backgroundColor: 'rgb(218, 209, 209)',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      height: '130px'
    },
    Title: {
      fontWeight: 'bold',
      color: 'black',
      fontSize: '2rem',
      padding: '0px',
      margin: '0px',
      marginSottom: '12px'
    },
    grid:{
      marginBottom: '20px'
    },
    MenuDetailHeader:{
      display: 'flex',
      alignItems: 'center',
      '& $hr':{
        borderLeft:'1px solid black',
        height: '15px',
      }
    },
    Link:{
      color:'black',
      margin: '10px',
      textDecoration: 'none'
    },
    titleText:{
      fontSize: '30px',
      color: 'black',
    },
    priceText:{
      fontSize: '24px',
      color: '#fe6c61',
      fontWeight:'bold'
    },
    descriptionText:{
      fontSize: '15px',
      color: 'grey',
    },
    ProductDetailColorContainer:{
      marginTop: '20px',
      display: 'flex',
      //padding: '10px',
      alignItems:'center',
      '& $a':{
          cursor: 'pointer',
          display: 'flex',
          alignItems:'center',
          justifyContent:'center',
          backgroundColor:'white',
          textDecoration:'none',
          color: 'black',
          width: '20px',
          height: '20px',
          marginLeft: '10px',
          borderRadius: '50%',
          border: '1px solid white',
          fontSize: '15px',
          '&:hover': {
            border: '1px solid black',
            color: 'white',
            backgroundColor: 'black'
          }
        },
    },
    ProductDetailTextId:{
      fontSize: '1rem',
      color: 'black',
      fontWeight:'bold'
    },
    ProductDetailColorItem:{
      cursor: 'pointer',
      display: 'block',
      border: 'none',
      width: '30px',
      height: '30px',
      marginLeft: '15px',
      marginBottom: '5px',
      borderRadius: '50%',
      '&:focus': {
        border: '2px solid #fe6c61',
        color: '#fe6c61'
      }
    },
    ProductDetailSizeItem:{
      cursor: 'pointer',
      display: 'block',
      width: '30px',
      height: '30px',
      marginLeft: '15px',
      marginBottom: '5px',
      '&:hover': {
        border: '1px solid #fe6c61',
        color: '#fe6c61'
      },
      '&:focus': {
        border: '2px solid #fe6c61',
        color: '#fe6c61',
        fontWeight:'bold'
      }
    },
    addCardContainer:{
      marginTop: '25px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      width: '100%',
      ['@media (max-width:780px)']: {
        flexWrap:"wrap",
     }
    },
    plusCard:{
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      backgroundColor: 'white',
      width: '120px',
      height: '45px',
      border: '1px solid lightgrey',
      ['@media (max-width:320px)']: {
        width:"100%",
     }
    },
    plusCardButton:{
      cursor: 'pointer',
      backgroundColor: 'white',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      width: '30px',
      height: '100%',
      color: 'black',
      fontSize: '1.2rem',
      '&:hover': {
        color: 'red'
      },
    },
    plusCardText:{
      fontSize: '1.3rem',
