import React from 'react';
import TextField from '@material-ui/core/TextField';
import { Grid,Box,Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import Map1 from '../components/Map';
import { contactData } from '../DATA';
import img2 from '../images/contact/ccontact1.jpg'

const useStyles = makeStyles((theme) => ({
  backgroundFixx: {
  },
 gridItem1: {
  height: "700px",
  width: "100%",
  backgroundImage: `url(${img2})`,
  backgroundSize: 'cover',
  backgroundPosition: 'center center',
  backgroundAttachment: 'fixed',
  backgroundRepeat: 'no-repeat',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center'
  },
  banner: {
    width: "100%",
    height: "100%",
    minHeight: '700px',
    objectFit: "cover",
    position: 'fixed',
    backgroundPosition: 'center center',
    left: 0,
    top: 0
},
  gridItemk: {
    backgroundColor: 'red',
    display: 'flex',
    alignItems: 'center'
  },
  formContainer: {
    padding: 20
  },
  form: {
    display:'flex',
    flexDirection:'column',
    width: '90%',
    padding: 20,
    marginTop: 20,
    '& .MuiTextField-root': {
      marginBottom: theme.spacing(3),
      width: '100%',
    },
    "& .MuiFormLabel-root": {
      color: "#1e1e27", 
      fontSize:"14px"  
    },
    ['@media (max-width:598px)']: {
      marginTop:"30px",
    }
  },
  input: {
    '&:after': {
      borderBottom:"2px solid #1e1e27",
      },   
  },
  centerTitleContainer: {
    position: 'absolute',
    top: 0,
    right: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, .5)',
    opacity: 0.9,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  },
  centerTitle: {
    width: 400,
    height: 150,
    backgroundColor: 'white',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1,
    '& h1': {
      fontSize: '37px',
      fontWeight: 500,
      color: 'black'
    },
    '& h3': {
      fontSize: '20px',
      fontWeight: 500,
      color: 'white',
      marginTop: 15
    }
  },
  wraper: {
    backgroundColor: 'white',
    display: 'flex',
    justifyContent: 'center',
    padding: '100px 0px'
  },
  contact: {
    display: 'flex',
    justifyContent: 'center',
    padding: 20
  },
  infoContainer: {
    //backgroundColor: 'green',
    marginTop: 20,
    marginLeft: 30,

  },
  contactInfo: {
    cursor: 'pointer',
    display: 'flex',
    alignItems: 'center',
    width: 'max-content',
    marginTop: 30,
    '&:hover': {
      '& $description, $iconCard': {
        color: '#F78181'
      }
    }
  },
  titleContainer: {
    padding: '20px 0px',
    transform: 'rotate(180deg)',
    writingMode: 'tb-rl',
    writingMode: 'vertical-rl',
    whiteSpace: 'nowrap',
    textAlign: 'center'
  },
  title: {
    fontSize: '50px',
    color: 'white',
    fontWeight: 500,
    textTransform: 'uppercase',
    fontFamily: 'Raleway, sans-serif',
  },
  description: {
    fontSize: '18px',
    fontFamily: 'Oregano, cursive',
    color: 'grey',
    textTransform: 'lowercase'
  },
  iconCard: {
    fontSize: '18px',
    marginRight: 15,
    fontFamily: 'Oregano, cursive',
    color: 'grey'
  },
  gridItem: {
    '@media (max-width:900px)': {
      marginTop: 20
    }
  }
}));

const Contact = () => {
  const classes = useStyles();
  return (
    <div>
      <Grid container xs={12} className={classes.backgroundFixx}>
        <Grid item xs={12} md={6} className={classes.gridItem1}>
        <h1 className={classes.title} >contact us</h1>
        </Grid>
        <Grid item xs={12} md={6} className={classes.gridItemk}>
          <Box className={classes.formContainer}>      
            <form className={classes.form}>                  
                    <TextField
                       name="fullname"
                       label="Full Name"
                       type="text"
                       InputProps={{
                        className: classes.input,
                       }}
                       />
                    <TextField
                       name="email"
                       label="Email"
                       type="email"
                       InputProps={{
                        className: classes.input,
                       }}
                       />
                    <TextField
                       label="Type your Message"
                       multiline
                       rows={4}
                       maxRows={4}
                       InputProps={{
                        className: classes.input,
                       }}
                       />                   
                    <button   
                        type='submit'
                        style={{marginTop:"10px"}}
                        variant="contained"
                        className={classes.button}
                        >SEND
                    </button>
            </form> 
          </Box>
        </Grid>
      </Grid>
      <Grid container xs={12} className={classes.backgroundFixx}>    
        <Grid item xs={12} md={6} className={classes.gridItemk} style={{display:'flex'}}>
         <Box className={classes.titleContainer}>
            <Typography className={classes.title}>{contactData[0].titleForm}</Typography>
          </Box>
        <Box className={classes.infoContainer}>
           <Box>
              {contactData[0].children.map((row) => (
                <Box className={classes.contactInfo}>
                  <Typography className={classes.description}>{row.title}</Typography>
                </Box>
              ))}
            </Box>
        </Box>
        </Grid>
        <Grid item xs={12} md={6} className={classes.gridItem1}>
        </Grid>
      </Grid>
      <Map1 />
      <Grid container xs={12} className={classes.gridItem1} />
    </div>
  );
};

export default Contact;


