import React,{useState, useEffect} from 'react';
import { Grid,Button, Typography,Box,Container} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import {useNavigate,useParams,Link} from 'react-router-dom';
import axios from "axios";
import Product from '../components/Product/Product';
import Pagination from '../components/Pagination';
import w from '../images/category/men11.jpg';
import paypal from '../images/creditCard/paypal22.png';
import amex from '../images/creditCard/amex1.png';
import viza from '../images/creditCard/visa-credit-card.png';
import skrill from '../images/creditCard/skrill.png';
import mastercard from '../images/creditCard/mastercard11.png';
import Close from '@material-ui/icons/Close';
import { useCart } from 'react-use-cart';
import { PregnantWoman } from '@material-ui/icons';


const useStyles = makeStyles((theme) => ({
    ProductDetailContainer: {
      backgroundColor: 'white',
      marginTop: '185px',
      ['@media (max-width:780px)']: { 
        marginTop: '130px',
      }
    },
    ProductDetailHeader: {
      backgroundColor: 'rgb(218, 209, 209)',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      height: '130px'
    },
    Title: {
      fontWeight: 'bold',
      color: 'black',
      fontSize: '2rem',
      padding: '0px',
      margin: '0px',
      marginSottom: '12px'
    },
    grid:{
      marginBottom: '20px'
    },
    MenuDetailHeader:{
      display: 'flex',
      alignItems: 'center',
      '& $hr':{
        borderLeft:'1px solid black',
        height: '15px',
      }
    },
    Link:{
      color:'black',
      margin: '10px',
      textDecoration: 'none'
    },
    container: {
      backgroundColor:'red',
      marginTop: '50px',
      //paddingBottom: '100px'
    },
    slideContainer: {
        display: 'flex',
        width: '100%',
        padding: 10,
        backgroundColor: 'yellow'
      },
      slideTitleContainer: {
        display: 'flex',
        flexDirection: 'column',
        marginRight: 20,
        backgroundColor: 'pink',
        borderRight: '1px solid lightgray'
      },
      slideTitle: {
        width: '200px',
        height: '50px',
        display: 'flex',
        padding: 10,
        alignItems: 'center',
        transition: 'all 1s ease-out'
      },
      slideTerms: {
        display: 'flex',
        alignItems: 'center',
        backgroundColor: 'pink'
      },
      title: {
        fontFamily: 'Raleway, sans-serif',
        fontSize: '18px',
        fontWeight: 500,
        color: 'black',
      },
      Icon: { 
        fontSize: 20,    
        color: "black",
        "&:hover":{
            color: "green",
          },
      },

  }));

  const items = [
    {
        id: 0,
        title: '300',
    },
    {
        id: 1,
        title: '400',
    },
    {
        id: 2,
        title: '500',
    },
    {
        id: 3,
        title: '600',
    }
]

const PromotionPage = () => {
    const classes = useStyles();
    const [products,setProducts] = useState([]);
    const [data,setData] = useState([])
    const [currentPage, setCurrentPage] = useState(1);
    const [postsPerPage] = useState(10);
    const [countdown, setCountdown] = React.useState(0);
    const [width, setWidth] = React.useState(window.innerWidth);
    const breakpoint = 780;
    React.useEffect(() => {
     const handleResizeWindow = () => setWidth(window.innerWidth);
      // subscribe to window resize event "onComponentDidMount"
      window.addEventListener("resize", handleResizeWindow);
      return () => {
        // unsubscribe "onComponentDestroy"
        window.removeEventListener("resize", handleResizeWindow);
      };
    }, []);


  React.useEffect(() => {
    const timeout = setTimeout(() => {
        if(countdown == items.length - 1){
            setCountdown(0);
        }else{
            setCountdown(countdown + 1);
        }
    }, 3000);
    return () => {
        clearTimeout(timeout);
    }
   }, [countdown]);



    const showProducts = () => {
        axios.get("https://fakestoreapi.com/products/")
        .then((response)=>{
          console.log('product',response.data)
          setProducts(response.data)
          setData(response.data)
        })
        .catch((err)=>{
          console.log("err", err)
      })
    }

useEffect(() => {
    showProducts();
  }, []);

  if (width <= breakpoint) {
    return (
      <div  className={classes.ProductDetailContainer}>
        <h3>Component 1</h3>
        <p>Current width is {width} px</p>
      </div>
    );
  }
    return (
        <Grid container  className={classes.ProductDetailContainer}>
           <Grid item  xs={12} sm={12}  className={classes.ProductDetailHeader}>   
             <h1  className={classes.Title}>Promotion</h1>
             <Box  className={classes.MenuDetailHeader}>
               <Link to='/' className={classes.Link}>Home</Link>
               <hr/>
               <Link to='#' className={classes.Link}>Promotion</Link>
             </Box>
          </Grid>
          <Container maxWidth="md" className={classes.container}>
           <Grid container>
           <div className={classes.slideContainer}>
          <div className={classes.slideTitleContainer}> 
            {items.map((index,i)=>{
              return(
                <div
                  className={classes.slideTitle} 
                  style={{
                    backgroundColor: index.id === countdown? 'lightgray' : 'white',
                    borderRight: index.id === countdown?  '9px solid blue' : '9px solid white'
                }}
                >
                  <h1 className={classes.title} style={{alignSelf: 'center'}}>{index.id}</h1>
                </div>
              )
            })}
          </div>
          <div className={classes.slideTerms}> 
            {items.map((index,i)=>{
              return(
                <>
                {index.id === countdown && (
                  <div>
                    <h1>{index.title}</h1>
                  </div>
                )}
                </>
              )
            })}
          </div>
        </div>
           </Grid>
         </Container>
        </Grid>
)}

export default PromotionPage;
