import Toolbar from '@material-ui/core/Toolbar';
import { makeStyles } from '@material-ui/core/styles';
import { AppBar } from '@material-ui/core';
import {Link} from "react-router-dom"
import {List, ListItemText, Box, useScrollTrigger, Typography} from "@material-ui/core";
import ShoppingCartOutlinedIcon from '@material-ui/icons/ShoppingCartOutlined';
import { Badge} from '@material-ui/core';
import { HeaderBottomMenu, HeaderTopMenu } from '../../DATA';
import SubjectIcon from '@material-ui/icons/Subject';
import { Search, Twitter, Instagram, Facebook} from '@material-ui/icons';
import CloseIcon from '@material-ui/icons/Close';
import { Delete } from '@material-ui/icons';
import { NavLink, useLocation, useNavigate } from 'react-router-dom';
import { useCart } from 'react-use-cart';
import img1 from '../../images/promotion/pro1.jpg';
import img2 from '../../images/promotion/pro2.jpg';
import axios from "axios"
import Product from '../Product/Product';

const useStyles = makeStyles((theme) => ({
  Icon: { 
    cursor: "pointer",
    marginRight: 15,
    fontSize: 18,    
    color: "black",
    transition: "all 0.5s ease-out",
    "&:hover":{
      color: "green",
    },
  },
  sidebarOverlay: {
    position: 'fixed',
    overflowX: 'hidden',
    top: 0,
    right: 0,
    height: '100%',
    width: '100%',
    zIndex: 1,
    pointerEvents: 'auto',
    visibility: 'hidden',
    backgroundColor: '#000',
    opacity: '0.75',
    '&::-webkit-scrollbar': {
      display: 'none',
      width:0,

    }
  },
  sidebar: {
    position: 'fixed',
    display: 'flex',
    flexDirection: 'column',
    overflowY: 'auto',
    overflowX: 'hidden',
    top: 0,
    backgroundColor: 'white',
    right: 0,
    height: '100%',
    width: '450px',
    //transform: 'translateX(0%)',
    zIndex: 2,
    transition: 'all 3s ease-in',
    '@media (max-width:768px)': {
      width: '100%'
    }
  },
  sidebarHeader: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 10,
    backgroundColor: "white",
    '& h3': {
      fontSize: "18px",
      fontWeight: 400,
      fontFamily: 'Montserrat, sans-serif',
      letterSpacing: '.2em',
      textTransform: 'uppercase',
      color: 'black'
    },
    '& button': {
      border: 'none',
      backgroundColor: 'transparent',
      padding: 10,
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center'    
    }
  },
  cartSidContainer: {
    display: 'flex',
    overflowY: 'auto',
    overflowX: 'hidden',
    maxHeight: '40vh',
    flexDirection: 'column',
    //backgroundColor: "black",
    padding: 10,
    flexGrow: 1

  },
  cart: {
    height: 100,
    backgroundColor: "white",
    boxShadow: '0 0 16px rgb(0 0 0 / 15%)',
    display: 'flex',
    padding: 10,
    marginTop: 15,
  },
  containerImage: {
    width: '100px',
    height: '100px',
    overflow: 'hidden',
    backgroundColor: "white",
  },
  image: {
    height: "100%",
    minWidth: '100%',
    objectFit: 'contain',
  },
  discreption: {
    display: 'flex',
    flexDirection: 'column',
    flex: 1,
    height: 'max-content',
    margin: '0px 5px',
    '& h2': {
      fontSize: "15px",
      fontWeight: 500,
      fontFamily: 'Montserrat, sans-serif',
      letterSpacing: '.1em',
      textTransform: 'none',
      color: 'black',
      margin: 0
    },
    '& h4': {
      fontSize: "15px",
      fontWeight: 400,
      fontFamily: 'Montserrat, sans-serif',
      letterSpacing: '.1em',
      textTransform: 'none',
      color: 'grey',
      margin: 0,
      marginTop: 10
    }
  },
  delete: { 
    fontSize: 20,    
    color: "grey",
    cursor: 'pointer'
    },
  cartTotal: {
    width: '100%',
    boxSizing: 'border-box',
    backgroundColor: "lightgray",
    padding: 20,
    paddingBottom: 20,
    marginTop: 15
  },
  cartTotalItem: {
    marginBottom: 10,
    width: '100%',
    height: '30px',
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    backgroundColor: 'white',
    borderBottom: '1px solid grey',
  },
  cartTotalItemTextT: {
    fontSize: "12px",
    fontWeight: 400,
    fontFamily: 'Montserrat, sans-serif',
    letterSpacing: '.1em',
    textTransform: 'uppercase',
    color: 'black',
  },
  cartTotalItemTextD: {
    fontSize: '12px',
    fontWeight: 400,
    fontFamily: 'Montserrat, sans-serif',
    letterSpacing: '.1em',
    textTransform: 'none',
    color: 'grey'
  },
  addCardButton:{
    marginTop: 20,
    cursor: 'pointer',
    backgroundColor: 'black',
    display: 'block',
    justifyContent: 'center',
    alignItems: 'center',
    padding: 10,
    width: '100%',
    fontFamily: 'Montserrat, sans-serif',
    letterSpacing: '.1em',
    color: 'white',
    fontSize: '12px',
    textTransform: 'uppercase',
    border: '2px solid transparent',
    '&:hover': {
      color: 'white',
      backgroundColor: '#007fbf',
    }
  },
  searchBar: {
    position: 'relative',
    transition: "all 3s ease",
    WebkitTransition: 'all 3s ease',
    mozTransition: 'all 3s ease',
    width: '90%',
    margin: '0 auto',
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'lightgray',
    padding: 5,
    '& span': {   
      cursor: "pointer",
      height: '100%',   
      marginLeft: 10
      },
  },
  closeSearch: {
    fontSize: 20,
    color: 'black',
    transition: "all 0.5s ease-out",
    "&:hover":{
      color: "red",
    },
  },
  inputSearch: {
    display: 'block',
    width: '90%',
    height: '30px',
    color: 'black',
    borderRadius: 0,
    border: 'none',
    backgroundColor: 'transparent',
    border: '1px solid white',
    '&:focus': {
      outline: 'none',
    }
  },
  resultContainer: {
    top: '100%',
    left: 0,
    position: 'absolute',
    width: '100%',
    height: '300px',
    display: "flex",
    backgroundColor: 'white',
    overflowY: 'scroll',
  },
  filtredProducts: {
    display: 'flex',
    flexDirection: 'column',
  }
  }))

const SidebarCart = ({ openCartFunction, open }) => {
    const classes = useStyles();
    const navigate = useNavigate();
    const {cartTotal, items} = useCart();

    return (
      <div className={classes.sidebar}
      style={{
        transform: open ? 'translateX(0%)' : 'translateX(100%)',
        transition: 'all 3s',
      }}
      >
        <div className={classes.sidebarHeader}>
          <h3>Cart</h3>
          <button onClick={openCartFunction}>
            <CloseIcon className={classes.Icon}/>
          </button>
        </div>
          <div className={classes.cartSidContainer}>
            {items.map((item,index)=>{
              return(
          <div className={classes.cart}>
            <div className={classes.containerImage}>
              <img src={item.image} className={classes.image}/>
            </div>
            <div className={classes.discreption}>
              <h2>{item.title}</h2>
              <h4>${item.price}</h4>
            </div>
            <Delete className={classes.delete}/>
          </div>
          )})}
        </div>
        <div  className={classes.cartTotal} >
           <div className={classes.cartTotalItem}>
             <Typography className={classes.cartTotalItemTextT}>Subtotal</Typography>
             <Typography className={classes.cartTotalItemTextD}>${(cartTotal).toFixed(2)}</Typography>
           </div>
           <div className={classes.cartTotalItem}>
             <Typography className={classes.cartTotalItemTextT}>Delivery</Typography>
             <Typography className={classes.cartTotalItemTextD}>$110.00</Typography>
           </div>
           <div className={classes.cartTotalItem}>
             <Typography className={classes.cartTotalItemTextT}>Total</Typography>
             <Typography className={classes.cartTotalItemTextD}>${(cartTotal + 110.00).toFixed(2)}</Typography>
           </div>
           <button className={classes.addCardButton}>PROCEED TO CHECKOUT</button>
           <button className={classes.addCardButton} onClick={()=> navigate('/cart')}>
             view card
           </button>
        </div>
      </div>
    )
  };

export default SidebarCart;
