import React, {useState,useEffect}from 'react';
import Toolbar from '@material-ui/core/Toolbar';
import { makeStyles } from '@material-ui/core/styles';
import { AppBar } from '@material-ui/core';
import {Link} from "react-router-dom"
import {List, ListItemText, Box, useScrollTrigger} from "@material-ui/core";
import ShoppingCartOutlinedIcon from '@material-ui/icons/ShoppingCartOutlined';
import { Badge} from '@material-ui/core';
import { Menu } from '../DATA';
import SubjectIcon from '@material-ui/icons/Subject';
import { Search, Twitter, Instagram, Facebook} from '@material-ui/icons';
import CloseIcon from '@material-ui/icons/Close';
import { NavLink, useLocation, useNavigate } from 'react-router-dom';
import { useCart } from 'react-use-cart';

const useStyles = makeStyles((theme) => ({
    Toolbar: {  
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      padding: "0px 7%",
      height: "120px",
      ['@media (max-width:320px)']: {        
        padding: "0px 7%",
      },
      ['@media (max-width:780px)']: {        
        justifyContent: 'space-between',
      }
    },
    Toolbar2: {  
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      height: "30px",
      padding: "10px 7%",
      borderBottom: '1px solid lightgray'
    },
    Logo: {
        color:  theme.palette.fontColor.primary,
        fontSize: "2rem",
        fontWeight: 500,
        marginLeft: 30,
        marginRight: 30,
        fontFamily: 'Noto Sans JP, sans-serif',
        ['@media (max-width:780px)']: {
          display: 'none'
        }
    },
    LogoMobile: {
      color:  theme.palette.fontColor.primary,
      fontSize: "2rem",
      fontWeight: 700,
      display: "none",
      ['@media (max-width:780px)']: {
        display: 'block'
      }
    },
    List: {
        display: "flex",
        alignItems: 'center',
        listStyle: "none",
        ['@media (max-width:780px)']: {        
            display: "none",
          }
    },
    ListMobile: {
        display: "none",
        transition: "display 3s ease-in",
        ['@media (max-width:780px)']: {
            position:"absolute",
            display: "block",
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-around',
            alignItems: 'center',
            backgroundColor: "lightgray",
            color:"black",
            right: "0px",
            top: "50px",
            width: "100%",
            height: 300,
            paddind: 10,
            textAlign: "center"
     } },
    li :{
        backgroundColor:  'blue',
    },
    links: {     
        fontSize: "15px",
        fontWeight: 400,
        transition: "all 0.5s ease-out",
        color:  theme.palette.fontColor.primary,
        cursor: "pointer",
        textDecoration: "none",
        margin: 15,
        fontFamily: 'Noto Sans JP, sans-serif',
        "&:hover":{
            color: "green",
        },
        ['@media (max-width:780px)']: {
          color: "black",
          margin: 0
        }
    },
    IconContainer: {     
      cursor: "pointer",
      marginRight: 15
    },
    Icon: { 
    fontSize: 20,    
    color: "black",
    "&:hover":{
        color: "green",
      },
    },
    mobileicon: {
        display: "none",
        transition: "0.5s ease-in",
        ['@media (max-width:780px)']: {
            display:" block",
            color: "black",
         }
    }
  }))


const Header = (props) => {
    const classes = useStyles();
    const [isMobile,setIsMobile] = useState(false);
    const [navBg, setNavBg] = useState("transparent");
    const navigate = useNavigate();
    let location = useLocation();
    const { totalItems } = useCart();

    const trigger = useScrollTrigger({
      disableHysteresis: true,
      threshold: 0,
      target: props.window ? window() : undefined
    });

    useEffect(() => {
      if (window.location.pathname != "/about" || window.location.pathname != "/contact"
      || window.location.pathname != "/" || trigger ) {
        setNavBg("white");
      } else {
        setNavBg('transparent')
      }
    }, [location]);

    return (
        <AppBar 
           style={{
            backgroundColor: navBg,
            transition: trigger ? '0.3s' : '0.5s',
            boxShadow:window.location.pathname != "/" || "/about" || "/contact" && "0 0 16px rgb(0 0 0 / 15%)"
          }}
           elevation={0}
           position="fixed"
        >
           <div className={classes.Toolbar2} style={{backgroundColor:navBg}}>
            <ul>
              <li className={classes.IconContainer}>
                <Facebook className={classes.Icon}/>
              </li>
              <li className={classes.IconContainer}>
                <Instagram className={classes.Icon}/>
              </li>
              <li className={classes.IconContainer}>
                <Twitter className={classes.Icon}/>
              </li>        
            </ul>
            <ul>
              <li className={classes.IconContainer}>
              <Search className={classes.Icon}/>
              </li>
              <li onClick={()=> navigate('/cart')} className={classes.IconContainer} style={{  marginRight: 0 }}>
              <Badge badgeContent={totalItems} color="secondary">
                <ShoppingCartOutlinedIcon className={classes.Icon}/>
              </Badge>
              </li>
            </ul>
          </div>
          <Toolbar className={classes.Toolbar } style={{backgroundColor:navBg}}>
            <h1 className={classes.LogoMobile}>Shop</h1>
            <ul className={isMobile ? `${classes.ListMobile}` : `${classes.List}`}>
              {Menu.map((item,index)=>{
                return(
                  <>
                  {index === 3 && (
                    <h1 className={classes.Logo}>FAKESTORE</h1>
                  )}
                  <li key={index}>
                    <Link to={item.url} className={classes.links}>{item.title}</Link>
                  </li>
                  </>
                )})}
            </ul>
            <div className={classes.mobileicon} onClick={() => setIsMobile(!isMobile)}>
               {isMobile ? (<CloseIcon/> ):(<SubjectIcon />)}
            </div>
          </Toolbar>

        </AppBar>

    )
}

export default Header



