import React,{useState, useEffect} from 'react';
import { Grid,Button, Typography,Box,Container} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import {useNavigate,useParams,Link} from 'react-router-dom';
import axios from "axios";
import Product from '../components/Product/Product';
import Pagination from '../components/Pagination';
import w from '../images/category/men11.jpg';
import paypal from '../images/creditCard/paypal.png'
import Close from '@material-ui/icons/Close';

const GenderMenu =[
  {
    id: "a",
    title: "All"
  },
  {
    id: "m",
    title: "Men",
    category : "men's clothing"
  },
  {
    id: "w",
    title: "Woman",
    category : "women's clothing"
  },
  {
    id: "k",
    title: "Kids",
  },
  {
    id: "j",
    title: "Jewelry",
    category : "jewelery"
  },
];

const ColorMenu =[
  {
    id: "#fdd4ce",
    title: "#fdd4ce",
  },
  {
    id: "black",
    title: "black",
  },
  {
    id: "grey",
    title: "grey",
  },
  {
    id: "pink",
    title: "pink",
  },
  {
    id: "#c0809c",
    title: "#c0809c",
  },
  {
    id: "#db8f8d",
    title: "#db8f8d",
  },
  {
    id: "lightgray",
    title: "lightgray",
  },
];

const useStyles = makeStyles((theme) => ({
    ProductDetailContainer: {
      backgroundColor: 'rgb(255, 255, 255)'
    },
    ProductDetailHeader: {
      backgroundColor: 'rgb(218, 209, 209)',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      height: '130px'
    },
    Title: {
      fontWeight: 'bold',
      color: 'black',
      fontSize: '2rem',
      padding: '0px',
      margin: '0px',
      marginSottom: '12px'
    },
    grid:{
      marginBottom: '20px'
    },
    MenuDetailHeader:{
      display: 'flex',
      alignItems: 'center',
      '& $hr':{
        borderLeft:'1px solid black',
        height: '15px',
      }
    },
    Link:{
      color:'black',
      margin: '10px',
      textDecoration: 'none'
    },
    container: {
      backgroundColor:'#fff',
      marginTop: '50px',
      paddingBottom: '100px'
    },
    gridContainer: {   
      padding: "0px 0%",
      display: "flex",
      justifyContent: "center",
      backgroundColor:'#fff',
      margin: '0 auto'
    },
    HeaderTable: {   
      backgroundColor:'#fff',
      borderBottom: '1px solid lightgrey',
      position: 'relative',
      '&:hover': {
        backgroundColor: '#f4f4f4',
        '& $RemoveIcon': {
          display: 'block'
        }
      },
    },
    HeaderItem: {   
      //backgroundColor: 'yellow',
      padding:10,
      display: 'flex',
      alignItems:'center',
      ['@media (max-width: 599px)']: {
        justifyContent: 'center'
      }
    },
    containerProduct: {
      display:'flex',
      flexDirection:'row',
      //backgroundColor:'pink',
      ['@media (max-width: 599px)']: {
        flexDirection: 'column',
        alignItems: 'center'
      }
    },
    containerImage: {
      width:100,
      height:100,
      //backgroundColor:'red',
      display:'flex',
      justifyContent:'center',
      alignItems:'center'
    },
    image: {
      width: '70%',
      height: "70%",
      objectFit:"cover",     
    },
    containerDescription: {
      display:'flex',
      flexDirection:'column',
      //backgroundColor:'grey',
      justifyContent:'center',
      marginLeft:10,
      ['@media (max-width: 599px)']: {
        alignItems: 'center'
      }
    },
    titleProduct: {
      fontSize: '14px',
      fontWeight: 500,
      color: 'black'
    },
    descriptionProduct: {
      fontSize: '13px',
      color: 'grey'
    },
    plusCard:{
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      backgroundColor: 'white',
      width: '100px',
      height: '40px',
      border: '1px solid lightgrey',
    },
    plusCardButton:{
      cursor: 'pointer',
      backgroundColor: 'white',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      width: '30px',
      height: '100%',
      color: 'black',
      fontSize: '1.2rem',
      '&:hover': {
        color: 'red'
      },
    },
    plusCardText:{
      fontSize: '18px',
      fontWeight: 'bolder',
      color: 'black'
    },
    TableHeaderTitle: {
      fontSize: '14px',
      fontWeight: 'bolder',
      color: 'black'
    },
    RemoveIcon: {
      display: 'none',
      position: 'absolute',
      top: 50,
      bottom: 50,
      right: 10,
      fontSize: '20px',
      color: '#CC0000',
      cursor: 'pointer',
      '&:hover': {
        color: 'red'
      },
    },
    cartTotal: {
      display: 'flex',
      flexDirection: 'column',
      backgroundColor: 'white',
      border: '1px solid lightgrey',
      padding: 20,
      ['@media (max-width: 960px)']: {
        marginTop: 50
      }
    },
    cartTotalTitle:{
      fontSize: '1.5rem',
      fontWeight: 'bolder',
      color: 'black'
    },
    cartTotalItem: {
      marginTop: 40,
      width: '100%',
      height: '30px',
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'space-between',
      backgroundColor: 'white',
      borderBottom: '1px solid lightgrey',
    },
    cartTotalItemText: {
      fontSize: '14px',
      color: 'grey'
    },
    addCardButton:{
      marginTop: 40,
      cursor: 'pointer',
      backgroundColor: 'black',
      display: 'block',
      justifyContent: 'center',
      alignItems: 'center',
      height: '45px',
      color: 'white',
      fontSize: '12px',
      fontWeight:'bold',
      border: '2px solid transparent',
      '&:hover': {
        color: 'white',
        backgroundColor: '#007fbf',
      },
      '&:focus': {
        color: 'white',
        backgroundColor: '#007fbf',
        border: '2px solid grey',
      },
     ['@media (max-width:320px)']: {
        width:"100%",
        marginLeft: '0px',
        marginTop: '20px',
     }
    },
    creditCardContainer: {
      marginTop: 40,
      width: '100%',
      display: 'flex',
      flexDirection: 'row',
    },
    creditCard: {
      height: 20,
      backgroundColor: 'black',
      marginLeft: 15,
    }
  }));

const Cart = () => {
    const classes = useStyles();
    const [products,setProducts] = useState([]);
    const [data,setData] = useState([])
    const [currentPage, setCurrentPage] = useState(1);
    const [postsPerPage] = useState(10);
    const [activeMenu, setActiveMenu] = useState('');
    const [activeMenu1, setActiveMenu1] = useState('');
     // Get current posts
  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = data.slice(indexOfFirstPost, indexOfLastPost);

  // Change page
  const paginate = pageNumber => setCurrentPage(pageNumber);

   //Filter for category
   const filterResult = (catItem) => {
    const result = products.filter((curDate) => {
        return curDate.category === catItem;
    });
    setData(result);
  };

    const showProducts = () => {
        axios.get("https://fakestoreapi.com/products/")
        .then((response)=>{
          console.log('product',response.data)
          setProducts(response.data)
          setData(response.data)
        })
        .catch((err)=>{
          console.log("err", err)
      })
    }

useEffect(() => {
    showProducts();
  }, []);


    return (
        <Grid container  className={classes.ProductDetailContainer}>
           <Grid item  xs={12} sm={12}  className={classes.ProductDetailHeader}>   
             <h1  className={classes.Title}>Cart</h1>
             <Box  className={classes.MenuDetailHeader}>
               <Link to='/' className={classes.Link}>Home</Link>
               <hr/>
               <Link to='#' className={classes.Link}>Cart</Link>
             </Box>
          </Grid>
          <Container className={classes.container}>
           <Grid container>
            <Grid container  item xs={12} md={8} className={classes.gridContainer}>
              <Grid item container xs={12} sm={12} className={classes.HeaderTable}>
                <Grid item xs={12} sm={5} className={classes.HeaderItem}>
                  <Typography className={classes.TableHeaderTitle}>PRODUCT</Typography>
                </Grid>
                <Grid item xs={12} sm={2} className={classes.HeaderItem}>
                  <Typography className={classes.TableHeaderTitle}>PRICE</Typography>
                </Grid>
                <Grid item xs={12} sm={3} className={classes.HeaderItem}>
                  <Typography className={classes.TableHeaderTitle}>QUANTITY</Typography>	
                </Grid>
                <Grid item xs={12} sm={2} className={classes.HeaderItem}>
                  <Typography className={classes.TableHeaderTitle}>SUBTOTAL</Typography>
                </Grid>    
              </Grid>
              <Grid item container xs={12} sm={12} className={classes.HeaderTable}>
                <Close className={classes.RemoveIcon}/>
                <Grid item xs={12} sm={5} className={classes.HeaderItem}>
                  <Box className={classes.containerProduct}>
                    <Box className={classes.containerImage}>
                      <img src={w}  className={classes.image}/>
                    </Box>
                    <Box className={classes.containerDescription}>
                      <Typography className={classes.titleProduct}>Basketball Sports Blue Shoes</Typography>
                      <Typography className={classes.descriptionProduct}>Size: Small</Typography>
                    </Box>
                  </Box>
                </Grid>
                <Grid item xs={12} sm={2} className={classes.HeaderItem}>
                  <Typography>$101.00</Typography>
                </Grid>
                <Grid item xs={12} sm={3} className={classes.HeaderItem}>
                  <Box className={classes.plusCard}>
                    <Box className={classes.plusCardButton}>-</Box>
                    <Typography className={classes.plusCardText}>1</Typography>
                    <Box className={classes.plusCardButton}>+</Box>
                  </Box>
                </Grid>
                <Grid item xs={12} sm={2} className={classes.HeaderItem}>
                  <Typography>$101.00</Typography>
                </Grid>
              </Grid> 



              <Grid item container xs={12} sm={12} className={classes.HeaderTable}>
                <Close className={classes.RemoveIcon}/>
                <Grid item xs={12} sm={5} className={classes.HeaderItem}>
                  <Box className={classes.containerProduct}>
                    <Box className={classes.containerImage}>
                      <img src={w}  className={classes.image}/>
                    </Box>
                    <Box className={classes.containerDescription}>
                      <Typography className={classes.titleProduct}>Basketball Sports Blue Shoes</Typography>
                      <Typography className={classes.descriptionProduct}>Size: Small</Typography>
                    </Box>
                  </Box>
                </Grid>
                <Grid item xs={12} sm={2} className={classes.HeaderItem}>
                  <Typography>$101.00</Typography>
                </Grid>
                <Grid item xs={12} sm={3} className={classes.HeaderItem}>
                  <Box className={classes.plusCard}>
                    <Box className={classes.plusCardButton}>-</Box>
                    <Typography className={classes.plusCardText}>1</Typography>
                    <Box className={classes.plusCardButton}>+</Box>
                  </Box>
                </Grid>
                <Grid item xs={12} sm={2} className={classes.HeaderItem}>
                  <Typography>$101.00</Typography>
                </Grid>
              </Grid> 




              <Grid item container xs={12} sm={12} className={classes.HeaderTable}>
                <Close className={classes.RemoveIcon}/>
                <Grid item xs={12} sm={5} className={classes.HeaderItem}>
                  <Box className={classes.containerProduct}>
                    <Box className={classes.containerImage}>
                      <img src={w}  className={classes.image}/>
                    </Box>
                    <Box className={classes.containerDescription}>
                      <Typography className={classes.titleProduct}>Basketball Sports Blue Shoes</Typography>
                      <Typography className={classes.descriptionProduct}>Size: Small</Typography>
                    </Box>
                  </Box>
                </Grid>
                <Grid item xs={12} sm={2} className={classes.HeaderItem}>
                  <Typography>$101.00</Typography>
                </Grid>
                <Grid item xs={12} sm={3} className={classes.HeaderItem}>
                  <Box className={classes.plusCard}>
                    <Box className={classes.plusCardButton}>-</Box>
                    <Typography className={classes.plusCardText}>1</Typography>
                    <Box className={classes.plusCardButton}>+</Box>
                  </Box>
                </Grid>
                <Grid item xs={12} sm={2} className={classes.HeaderItem}>
                  <Typography>$101.00</Typography>
                </Grid>
              </Grid> 
           </Grid>
           <Grid container  item xs={12} md={3} className={classes.cartTotal}>
             <Typography className={classes.cartTotalTitle}>CART TOTALS</Typography>
             <Box className={classes.cartTotalItem}>
               <Typography className={classes.cartTotalItemText}>Subtotal</Typography>
               <Typography className={classes.cartTotalItemText}>$3,110.00</Typography>
             </Box>
             <Box className={classes.cartTotalItem}>
               <Typography className={classes.cartTotalItemText}>Delivery</Typography>
               <Typography className={classes.cartTotalItemText}>$3,110.00</Typography>
             </Box>
             <Box className={classes.cartTotalItem}>
               <Typography className={classes.cartTotalItemText}>Total</Typography>
               <Typography className={classes.cartTotalItemText}>$3,110.00</Typography>
             </Box>
             <button className={classes.addCardButton}>PROCEED TO CHECKOUT</button>
             <Typography className={classes.cartTotalItemText}>We accepted</Typography>
             <Box className={classes.creditCardContainer}>
               <img src={paypal} className={classes.creditCard} />
               <img src="https://media.jimmychoo.com/image/upload/w_auto,c_scale,q_auto:best,f_auto/ROWPROD_CONTENT/images/checkout/visa.jpg" className={classes.creditCard} />
               <img src="https://media.jimmychoo.com/image/upload/w_auto,c_scale,q_auto:best,f_auto/ROWPROD_CONTENT/images/checkout/paypal.jpg" className={classes.creditCard} />
               <img src="https://media.jimmychoo.com/image/upload/w_auto,c_scale,q_auto:best,f_auto/ROWPROD_CONTENT/images/checkout/mastercard.jpg" className={classes.creditCard} />
             </Box>
           </Grid>
           </Grid>
         </Container>
        </Grid>
)}

export default Cart;
