import React,{useState, useEffect} from 'react'
import { Link, useNavigate } from "react-router-dom"
import axios from "axios"
import { Grid,Button, Typography,Box,Container} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { LocationOn, Style } from '@material-ui/icons';
import Carousel from 'react-material-ui-carousel'
import Slider from "react-slick";
import Product from './Product';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const useStyles = makeStyles((theme) => ({
    PromotionContainer: {
        backgroundColor: 'grey',
        display: "flex",
        justifyContent:'center', 
    },
    PromotionItem: {
      backgroundColor: 'red',
    },
    carousel: {
        width: '100%',
        height: '100%', 
    },
    containerItem: {
        position: 'relative', 
        backgroundColor : 'red',
        display: "flex",
        justifyContent:'center',   
        alignItems:'center',  
    },
    imageItem: {
        minWidth:'250px',
        minHeight:'300px',
        width: '100%',
        height: '500px',
        objectFit:"cover"    
    },
    overlay: {
        bottom: 0,
        margin: '0px auto',   
        position: 'absolute',
        width: '70%',
        height: '20%',
        background : 'rgba(0, 0, 0, 0.1)',
        borderTopLeftRadius: 10,
        borderTopRightRadius: 10,    
        display:"flex",
        justifyContent:"center",
        alignItems:"center",
        animation: "$animWidth 2s forwards"
      },
    priceItem: {
        fontSize: '20px',
        color: 'white',
        textDecoration: 'none'
    },
    containerTitlePromotion: {
      //backgroundColor: 'red',
      padding: 20,
    },
    titlePromotion: {
        fontSize: '40px',
        fontFamily: 'Raleway, sans-serif',
        fontWeight: 500,
        color: '#333333',
    },
    descriptionPromotion: {
        marginTop: 20,
        fontFamily: 'Raleway, sans-serif',
        lineHeight: '35px',
        fontSize: '18px',
        color: 'grey',
    },
    buttonItem: {    
        transition: "all 0.3s ease 0s",
        cursor: 'pointer',
        backgroundColor: 'black',
        display: 'block',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '5px 20px',
        textDecoration: 'none',
        '& p': {
            color: 'white',
            fontSize: '8px',
            fontWeight:'bold'
          },
        '&:hover': {
          color: 'white',
          backgroundColor: '#007fbf',
        }
    },
    "@keyframes animWidth": {
        from: { height:'20%' },
        to: { height: '50%' }
    }
}));

const items = [
    {
        price: '300',
        imageUrl: '/static/promotion/pro9.jpg',
    },
    {
        price: '400',
        imageUrl: '/static/promotion/pro10.jpg',
    },
    {
        price: '500',
        imageUrl: '/static/promotion/pro11.jpg',
    },
    {
        price: '600',
        imageUrl: '/static/promotion/pro12.jpg',
    },
    {
        price: '700',
        imageUrl: '/static/promotion/pro4.jpg',
    },
];



export default function SlickProduct() {
  const classes = useStyles();
  const [products,setProducts] = useState([])

  const showProducts = () => {
      axios.get("https://fakestoreapi.com/products?limit=8")
      .then((response)=>{
        console.log('product',response.data)
        setProducts(response.data)
      })
      .catch((err)=>{
        console.log("err", err)
    })
  }

useEffect(() => {
  showProducts();
}, []);

  return (
    <Grid xs={12} container className={classes.PromotionContainer}>
      <Grid item xs={12} md={12}className={classes.PromotionItem}>
        <Slider 
          autoplay={true}
          dots
          infinite={true}
          speed={500}
          arrows
          slidesToShow= {1}
          slidesToScroll= {1}
          variableWidth= {true}
        >
            {products.map((index,i)=>{
                return(
                  <>
                  <div style={{ width: 100, backgroundColor: 'blue' }}>
                  <p>100</p>
                </div>
                <div style={{ width: 200 }}>
                  <p>200</p>
                </div>
                <div style={{ width: 75 }}>
                  <p>75</p>
                </div>
                <div style={{ width: 300 }}>
                  <p>300</p>
                </div>
                <div style={{ width: 225 }}>
                  <p>225</p>
                </div>
                </>
                )         
            })}
        </Slider>
      </Grid>
    </Grid>
  );
}
