import {forwardRef, useImperativeHandle, useRef} from 'react';
import { Grid,Button, Typography,Box,Container, TextField,IconButton, InputAdornment} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import * as Yup from 'yup';
import { useFormik, Form, FormikProvider } from 'formik';
import {useNavigate,useParams,Link} from 'react-router-dom';
import axios from "axios";
import Close from '@material-ui/icons/Close';
import { useCart } from 'react-use-cart';
import { Visibility, VisibilityOff } from '@material-ui/icons';

const useStyles = makeStyles((theme) => ({
  form: {
    display:'flex',
    flexDirection:'column',
    width: '90%',
    padding: 20,
    marginTop: 20,
    '& .MuiTextField-root': {
      marginBottom: theme.spacing(3),
      width: '100%',
    },
    "& .MuiInput-underline:before": {
      borderBottom: "2px solid lightgrey"
    },
    "& .MuiInput-underline:after": {
      borderBottom: "2px solid #9d8d6c"
    },
    "& .MuiInput-underline:hover:before": {
      borderBottom: "2px solid #9d8d6c"
    },
    "& .MuiFormLabel-root": {
      color: "#9d8d6c", 
      fontSize:"14px",
      fontFamily: "Montserrat, sans-serif",
      letterSpacing: "1px",
      fontWeight: 500,
      textTransform: "uppercase"
    },
    ['@media (max-width:598px)']: {
      marginTop:"30px",
    } 
  },
    ProductDetailContainer: {
        backgroundColor: 'white',
        marginTop: '185px',
        '@media (max-width:780px)': { 
          marginTop: '130px',
        }
      },
      ProductDetailHeader: {
        backgroundColor: 'rgb(218, 209, 209)',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        height: '130px'
      },
      Title: {
        fontWeight: 'bold',
        color: 'black',
        fontSize: '2rem',
        padding: '0px',
        margin: '0px',
        marginSottom: '12px'
      },
      grid:{
        marginBottom: '20px'
      },
      MenuDetailHeader:{
        display: 'flex',
        alignItems: 'center',
        '& $hr':{
          borderLeft:'1px solid black',
          height: '15px',
        }
      },
      Link:{
        color:'black',
        margin: '10px',
        textDecoration: 'none'
      },
      container: {
        //backgroundColor:'red',
        marginTop: '50px',
        paddingBottom: 100,
        minHeight: 500
      },
      formContainer: {
        //backgroundColor: 'pink',
        display: 'flex',
        position: 'relative',
        width: '100%',
        minHeight: 400,
        boxShadow:  "0 0 16px rgb(0 0 0 / 15%)" 
      },
      login: {
        //backgroundColor:'yellow',
        position: 'relative',
        left: 0,
        top: 0,
        width: '70%',
        height: '100%',
        boxSizing: 'border-box',
        padding: 10,
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column',
        '@media (max-width:780px)': {
          position: 'relative',
          width: '100%',
        },
      },
      banner: {
        backgroundColor: 'rgba(0, 0, 0, .5)',
        backgroundAttachment: 'fixed',
        position: 'absolute',
        top: 0,
        width: '30%',
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column',
        boxSizing: 'border-box',
        padding: 10,
        height: '100%',
        right:  0, 
        '@media (max-width:780px)': {
            display: 'none'
        },
      },
      bannerMobile: {
        backgroundColor:'lightgray',
        marginTop: 30,
        width: '100%',
        display: 'none',
        '@media (max-width:780px)': {
            display: 'flex',
            justifyContent: 'center'
        },
      },
      inputGroup: {
        //backgroundColor:'green',
        marginTop: 20,
        width: '70%',
        display: 'flex',
        flexDirection: 'column',
        '@media (max-width:780px)': {
           width: '100%',
        },
      },
      input: {
        width: '100%',
        margin: '10px 0px',
      },
      submitButton: {
        cursor: 'pointer',
        display: 'block',
        backgroundColor: 'black',
        width: '70%',
        height: 40,
        padding: '5px',
        border: 'none',  
        outline: 'none',     
        margin: '30px auto',
        color: 'white',
        fontWeight: 500,
        textTransform: 'uppercase',
        letterSpacing: '.1em',
        fontFamily: 'Raleway, sans-serif',
        '&:focus': {
          outline: 'none'
        },
      },
      bannerButton: {
        cursor: 'pointer',
        display: 'block',
        backgroundColor: 'transparent',
        width: '40%',
        height: 40,
        borderRadius: 25,
        padding: '5px',
        border: '1px solid white',  
        outline: 'none',     
        margin: '30px auto',
        fontSize: '14px',
        color: 'white',
        fontWeight: 500,
        textTransform: 'uppercase',
        letterSpacing: '.1em',
        fontFamily: 'Raleway, sans-serif',
        '&:focus': {
          outline: 'none'
        },
      },
      checkbox: {
        margin: '10px 5px 10px 0px',
        accentColor: 'black',
        width: 13,
        height: 13
      },
      checkboxText: {
        fontSize: '12px',
        color: 'black',
        fontWeight: 500,
        fontFamily: 'Raleway, sans-serif',
        transition: '1.2s ease-in-out'
      },
      formTitle: {
        fontSize: '24px',
        color: 'black',
        fontWeight: 500,
        textTransform: 'uppercase',
        fontFamily: 'Raleway, sans-serif',
        transition: '1.2s ease-in-out'

      },
      bannerTitle: {
        fontSize: '14px',
        color: 'white',
        fontWeight: 500,
        textTransform: 'none',
        letterSpacing: '.1em',
        fontFamily: 'Raleway, sans-serif',
      }
}))

const LoginForm = forwardRef((props, ref) => {
    const classes = useStyles();
    const [login, setLogin] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const RegisterSchema = Yup.object().shape({
      username: Yup.string().min(4, 'Too Short!').max(50, 'Too Long!').required('Username required'),
      email: Yup.string().email('Email must be a valid email address').required('Email is required'),
      password: Yup.string().required('Password is required')
    });

    const formik = useFormik({
      initialValues: {
        username: '',
        email: '',
        password: ''
      },
      validationSchema: RegisterSchema,
      validateOnChange: false,
      validateOnBlur: false,
      onSubmit: (values) => {
       console.log('data', values);
      }
    });


    const { errors, touched, isSubmitting, getFieldProps, setFieldValue, resetForm } = formik;

    useImperativeHandle(ref, () => ({
      reset() {
        resetForm();
      }
    }));

  return(
    <FormikProvider value={formik}>
      <Form
            className={classes.form}
            autoComplete="off"
            noValidate
            onSubmit={formik.handleSubmit}
        >
            <TextField
              name="email"
              label="Email"
              type="email"
              className={classes.input}
              InputProps={{
                className: classes.input,
              }}
              {...getFieldProps('email')}
              error={Boolean(touched.email && errors.email)}
              helperText={touched.email && errors.email}
            />
            <TextField
              name="password"
              label="Password"
              type={showPassword ? 'text' : 'password'}
              className={classes.input}
              {...getFieldProps('password')}
              InputProps={{
                className: classes.input,
                endAdornment: (
                  <InputAdornment position="end">
                    <IconButton edge="end" onClick={() => setShowPassword((prev) => !prev)}>
                      {showPassword ? <Visibility /> : <VisibilityOff />}
                    </IconButton>
                  </InputAdornment>
                )
              }}
              error={Boolean(touched.password && errors.password)}
              helperText={touched.password && errors.password}
            />
            <div style={{display: 'flex',justifyContent:'space-between', alignItems: 'center'}}>
              <div>
                <input className={classes.checkbox} type="checkbox" />
                <span className={classes.checkboxText}>Remember Me</span>
              </div>
              <span className={classes.checkboxText}>Forgot password?</span>
            </div>
            <button className={classes.submitButton} type="submit" onClick={onsubmit}>
              Login
            </button>
        </Form>
      </FormikProvider>
    )
})

export default LoginForm
