import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Typography, Grid, Paper, Container, Box } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import k from '../images/category/women2.jpg';
import m from '../images/about/ab.jpg';

const useStyles = makeStyles((theme) => ({
  backgroundFixu: {
    height: '500px',
    width: '100%',
    backgroundImage: `url(${k})`,
    backgroundSize: 'cover',
    backgroundAttachment: 'fixed',
    //backgroundColor: 'black',
    zIndex: 2,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  centerTitle: {
    fontSize: '37px',
    fontWeight: 500,
    color: 'white',
    marginTop: 50
  },
  gridCenterContainer: {
    backgroundColor: 'white',
    margin: '0 auto',
    marginTop: -150,
    minHeight: '400px',
    display: 'flex',
    flexDirection: 'column',
    boxShadow: '0 10px 25px 0 rgb(1 45 126 / 15%)'
  },
  descriptionText: {
    padding: 30
  },
  text: {
    fontSize: '16px',
    fontWeight: 400,
    lineHeight: '2rem',
    color: '#4c494a',
    marginTop: 10,
    fontFamily: 'Noto Sans JP, sans-serif',
    '& a': {
      color: '#39B3BA'
    }
  },
  Overlay: {
    height: '500px',
    width: '100%'
  },
  backgroundAbout: {
    width: '100%',
    height: '100%',
    objectFit:'cover'
  }
}));

const Aboutk = () => {
  const classes = useStyles();
  return (
    <div>
      <Grid item xs={12} className={classes.backgroundFixu}>
        <h1 className={classes.centerTitle}>About Us</h1>
      </Grid>
      <Grid xs={10} sm={8} className={classes.gridCenterContainer}>
        <Box className={classes.Overlay}>
          <img src={m} className={classes.backgroundAbout}/>
        </Box>
        <Box className={classes.descriptionText}>
          <Typography className={classes.text}>
          Our goal is to encourage and motivate the everyday woman by giving her the tools to overcome everyday challenges and meet women who have faced and overcome the same challenges. By helping one another, women can transform not only our peers but the world.
          When women feel confident about their bodies, their worthiness, and their intelligence, they can go into the world and express themselves without intimidation. We believe thereâ€™s no better time than now.<a href="#">smileland.com</a>.
          </Typography>
        </Box>
      </Grid>
    </div>
  );
};

export default Aboutk;
