import React from 'react'
import { Grid } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles';
import img from "../images/hero/hero15.jpg";
import ButtonArow from './ButtonArow/ButtonArow';
import { HeroData } from '../DATA';
import Button from '@material-ui/core/Button';
import KeyboardDoubleArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import { Link } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
    GridContainer: {
        position: 'relative',  
    },
    GridButton: {
        position: 'absolute',
        top: '72%',
        width: '100%',
        display:'flex',
        justifyContent:'center'
    },
    Button : {
        display: "flex",       
        flexDirection: 'column',
        borderRadius: '100%',
        width:'80px',
        height:'80px',
        backgroundColor:'black',
        color:'white'
    },
    banner: {
        clipPath: "polygon(100% 0, 100% 50%, 50% 70%, 0 50%, 0 0)",
        width: "100%",
        //height: "100%",
        minHeight: '900px',
        objectFit:"cover",
        backgroundPosition: 'right'
    },
    overlay: {
        top: 0,
        left: 0,
        position: 'absolute',
        width: '100%',
        height: '100%',
        display: "flex",
        justifyContent:"flex-start",
        alignItems:"center",
      },
    content: {
        height: 'max-content',
        width: 'max-content',
        marginBottom:'300px',
        marginLeft: '10%',
    },
    contentText :{
        fontSize: "45px",
        fontWeight: 500,
        color:'white',
        margin: 0,
        textTransform: "uppercase",
        fontFamily: 'Raleway, sans-serif',
        ['@media (max-width:780px)']: {        
            fontSize: "30px",
          },
        ['@media (max-width:400px)']: {        
              fontSize: "25px",
            }
    },
    contentText1 :{
        fontSize: "18px",
        fontWeight: 500,
        color:'white',
        textTransform: "none",
        marginBottom: "20px",
        fontFamily: 'Raleway, sans-serif',
        ['@media (max-width:780px)']: {        
            fontSize: "15px",
            marginBottom: "20px",
          },
        ['@media (max-width:400px)']: {        
            fontSize: "12px",
            marginBottom: "10px",
          }
    },
    scrollTopButton : {
        display: "flex", 
        justifyContent: 'center',
        alignItems: 'center',      
        borderRadius: '50%',
        width:'30px',
        height:'50px',
        backgroundColor:'black',
        color:'white',
        outline: 'none',
        border: 0,
        cursor: 'pointer',
        zIndex: 20,
        transition: 's'
      },
        icon: { 
        fontSize: 35,    
        color: "white",
        transition: '2s',
        "&:hover":{
            color: "green",
          },
        },
}))
const Hero = () => {
    const classes = useStyles();

    return (
         <Grid container className={classes.GridContainer}>
              <img src={img} className={classes.banner}/>
              <div className={classes.overlay}>
             {HeroData.map((item,index)=>{
                        return(
                        <div className={classes.content}>
                           <h5 className={classes.contentText1}>{item.content1}</h5>
                           <h1 className={classes.contentText}>{item.content2}</h1>
                           <h1 className={classes.contentText1}>{item.content3}</h1>
                        </div>)}
                        )} 
                        </div>
                        <div className={classes.GridButton}>
                          <a href="#sh" className={classes.center}>
                            <button className={classes.scrollTopButton}>
                              <KeyboardDoubleArrowDownIcon className={classes.icon} />
                            </button>
                          </a>    
                        </div>
                        </Grid>
                        )
                    }

export default Hero
