import React, {useState,useEffect}from 'react';
import Toolbar from '@material-ui/core/Toolbar';
import { makeStyles } from '@material-ui/core/styles';
import { AppBar } from '@material-ui/core';
import {Link} from "react-router-dom"
import {List, ListItemText, Box, useScrollTrigger} from "@material-ui/core";
import ShoppingCartOutlinedIcon from '@material-ui/icons/ShoppingCartOutlined';
import { Badge} from '@material-ui/core';
import { Menu } from '../DATA';
import SubjectIcon from '@material-ui/icons/Subject';
import { Search, Twitter, Instagram, Facebook} from '@material-ui/icons';
import CloseIcon from '@material-ui/icons/Close';
import { NavLink, useLocation, useNavigate } from 'react-router-dom';
import { useCart } from 'react-use-cart';

const useStyles = makeStyles((theme) => ({
    Toolbar: {  
      display: 'flex',
      justifyContent: 'space-between',
      flexDirection: 'column',
      alignItems: 'center',
      padding: "0px 5%",
      backgroundColor: 'pink',
      //height: "100px",
      ['@media (max-width:320px)']: {        
        padding: "0px 5%",
      },
      ['@media (max-width:780px)']: {        
        justifyContent: 'space-between',
      }
    },
    Toolbar2: {  
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      height: "30px",
      padding: "10px 5%",
    },
    logoContainer: {  
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      height: "30px",
      width: '100%',
      padding: "5px 0",
      margin: '10px',
    },
    Logo: {
        fontSize: "35px",
        fontWeight: 400,
        textAlign: 'center',
        fontFamily: 'Italianno, cursive',
        ['@media (max-width:780px)']: {
          display: 'none'
        }
    },
    LogoMobile: {
      color:  theme.palette.fontColor.primary,
      fontSize: "2rem",
      fontWeight: 700,
      display: "none",
      ['@media (max-width:780px)']: {
        display: 'block'
      }
    },
    List: {
        display: 'flex',
        flex: '1 0 0',
        alignItems: 'center',
        listStyle: "none",
        margin: '0px',
        ['@media (max-width:780px)']: {        
            display: "none",
          }
    },
    ListMobile: {
        display: "none",
        transition: "display 3s ease-in",
        zIndex: 0,
        ['@media (max-width:780px)']: {
            position:"absolute",
            display: "block",
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-around',
            alignItems: 'center',
            backgroundColor: "lightgray",
            color:"black",
            right: "0px",
            top: "50px",
            width: "100%",
            paddind: 10,
            textAlign: "center"
     } },
    li:{
      position: 'relative',
      margin: '0 0px',
      '&:hover':{
        '& $dropdown':{
          display: "block",
          visibility: 'visible',
          opacity: 1
        },
      },
    },
    dropdown: {
      position: 'absolute',
      display: 'block',
      opacity: 0,
      top: '100%',
      minWidth: '150px',
      width: 'max-content',
      textAlign: 'left',
      visibility: 'hidden',
      transition: "all .3s ease-in",
      zIndex: 1,
      border: '1px solid lightgray',
      borderTop: 'none',
      borderBottom: 'none',
      '& li': {
        width: '100%',
        borderBottom: '1px solid pink',
        '& a': {
          display: 'block', 
          padding: '20px 10px',
          fontSize: "12px",
          fontWeight: 400,
          color: 'grey',
          transition: "all .3s ease-in",
          cursor: "pointer",
          textDecoration: "none",
          fontFamily: 'Montserrat, sans-serif',
          letterSpacing: '.2em',
          textTransform: 'uppercase',
          "&:hover":{ 
            backgroundColor: 'lightgray',
            color: 'black'
          },
      },
      },
     },
    links: {
        position: 'relative',
        display: 'block', 
        fontSize: "12px",
        fontWeight: 400,
        transition: "all 0.5s ease-out",
        cursor: "pointer",
        textDecoration: "none",
        fontFamily: 'Montserrat, sans-serif',
        letterSpacing: '.2em',
        textTransform: 'uppercase',
        "&:after" :{  
          position: 'absolute',
          content: '""',
          display:"pink",
          left: 0,
          bottom: 0,
          width: "0px",
          height: "3px",
          background: 'red',
          transition:" 0.3s ease 0s, left 0.3s ease 0s",
        },
        "&:hover:after":{ 
          width: "100%",
          left: "0",

        },
    },
    colorTextLight: {
      color: 'black',
      "&:hover":{
        color: "green",
      },
      ['@media (max-width:780px)']: {
        color: "black",
      }
    },
    colorTextDark: {
      color:  'white',
      "&:hover":{
        color: "green",
      },
      ['@media (max-width:780px)']: {
        color: "black",
      }
    },
    IconContainer: {     
      cursor: "pointer",
      marginRight: 15
    },
    Icon: { 
    fontSize: 20,    
    color: "black",
    "&:hover":{
        color: "green",
      },
    },
    mobileicon: {
        display: "none",
        transition: "0.5s ease-in",
        ['@media (max-width:780px)']: {
            display:" block",
            color: "black",
         }
    }
  }))


const Header = (props) => {
    const classes = useStyles();
    const [isMobile,setIsMobile] = useState(false);
    const [navBg, setNavBg] = useState(false);
    const [navDrop, setNavDrop] = useState(false);
    const [colorT, setColorT] = useState(false);
    const [navbarOpen, setNavbarOpen] = useState(false);

    const navigate = useNavigate();
    let location = useLocation();
    const { totalItems } = useCart();

    const trigger = useScrollTrigger({
      disableHysteresis: true,
      threshold: 0,
      target: props.window ? window() : undefined
    });

    useEffect(() => {
      if (window.location.pathname === "/about" || window.location.pathname === "/contact" || window.location.pathname === "/" ) {
        setNavBg("transparent");
        setColorT("colorTextDark");
      }  else {
        setNavBg('white');
        setColorT("colorTextLight");
      }
      if (window.location.pathname === "/about" || window.location.pathname === "/" ) {
        setNavDrop("black");
      }  else {
        setNavDrop('white');
      }
    }, [location]);

    if(!navBg || !navDrop || !colorT) {
      return null
    }

    return (
        <AppBar 
           style={{
            backgroundColor: trigger ? 'white' : navBg,
            transition: trigger ? '0.3s' : '0.5s',
            boxShadow: trigger ?  "0 0 16px rgb(0 0 0 / 15%)" :
            (window.location.pathname === "/about" || window.location.pathname === "/contact" || window.location.pathname === "/") ? 
             undefined : "0 0 16px rgb(0 0 0 / 15%)"
          }}
           elevation={0}
           position="fixed"
        >
           <div className={classes.Toolbar2} style={{backgroundColor: trigger ? 'white' : navBg, transition: trigger ? '0.3s' : '0.5s',}}>
            <ul>
              <li className={classes.IconContainer}>
                <Facebook className={classes.Icon}/>
              </li>
              <li className={classes.IconContainer}>
                <Instagram className={classes.Icon}/>
              </li>
              <li className={classes.IconContainer}>
                <Twitter className={classes.Icon}/>
              </li>        
            </ul>
            <ul>
              <li className={classes.IconContainer}>
              <Search className={classes.Icon}/>
              </li>
              <li onClick={()=> navigate('/cart')} className={classes.IconContainer} style={{ marginRight: 0 }}>
              <Badge badgeContent={totalItems} color="secondary">
                <ShoppingCartOutlinedIcon className={classes.Icon}/>
              </Badge>
              </li>
            </ul>
          </div>
          <div className={classes.Toolbar } style={{backgroundColor:trigger ? 'white' : navBg, transition: trigger ? '0.3s' : '0.5s',}}>
            <h1 className={classes.LogoMobile}>Shop</h1>
            <div className={classes.logoContainer}>
            <h1
              className={classes.Logo}
              style={{ color: trigger ? 'black' :
                (window.location.pathname === "/about" || window.location.pathname === "/contact" || window.location.pathname === "/") ?  
                  'white' : 'black'
               }}
            >
              ShopTemplate
            </h1>
            </div>
            <ul className={isMobile ? `${classes.ListMobile}` : `${classes.List}`}>
              {Menu.map((item,key)=>{
                  return(
                    <>
                  <li key={key} className={classes.li}>
                    <Link 
                      to={item.url}
                      style={{ padding: trigger ? '20px 20px' : '30px 20px'}}
                      className={
                        `${classes.links}
                         ${trigger ? classes.colorTextLight :
                         (window.location.pathname === "/about" || window.location.pathname === "/contact" || window.location.pathname === "/") ?  
                         classes.colorTextDark : classes.colorTextLight} 
                        `
                      }
                    >
                      {item.title}
                    </Link>
                    {item.children && (
                      <ul
                        className={classes.dropdown}
                        style={{ backgroundColor: trigger ? 'white' : navDrop }}
                      >
                         {item.children.map((row)=>{
                          return(
                            <li>
                              <a href={row.url}>{row.title}</a>
                            </li>
                          )})}
                      </ul>
                    )}
                  </li>
                  </>
                )})}
            </ul>
            <div className={classes.mobileicon} onClick={() => setIsMobile(!isMobile)}>
               {isMobile ? (<CloseIcon/> ):(<SubjectIcon />)}
            </div>
          </div>

        </AppBar>

    )
}

export default Header



