import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Typography, Grid, Paper, Container, Box } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  backgroundFix: {
    height: '300px',
    width: '100%',
    backgroundColor: '#F78181',
    backgroundAttachment: 'fixed',
    zIndex: 2,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  },
  centerTitleContainer: {
    position: 'absolute',
    top: 0,
    right: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'transparent',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  },
  centerTitle: {
    fontSize: '37px',
    fontWeight: 500,
    color: 'black'
  },
  gridCenterContainer: {
    backgroundColor: 'white',
    margin: '0 auto',
    marginTop: -100,
    minHeight: '400px',
    padding: 30,
    alignItems: 'center',
    boxShadow: '0 10px 25px 0 rgb(1 45 126 / 15%)'
  },
  gridContainer: {
    // backgroundColor: 'pink',
    display: 'flex',
    alignItems: 'center',
    marginBottom: 50,
    marginTop: 100
  },
  text: {
    fontSize: '16px',
    fontWeight: 400,
    lineHeight: '2rem',
    color: '#4c494a',
    marginTop: 10,
    fontFamily: 'Open Sans, sans-serif',
    '& a': {
      color: '#39B3BA'
    }
  },
  title: {
    fontFamily: 'Open Sans, sans-serif',
    fontSize: '19px',
    fontWeight: 'bold',
    color: '#F78181',
    marginBottom: 10
  },
  list: {
    paddingLeft: 35,
    '& li': {
      fontSize: '16px',
      fontWeight: 400,
      lineHeight: '2rem',
      color: '#4c494a',
      marginTop: 10,
      fontFamily: 'Open Sans, sans-serif'
    }
  }
}));

const About1 = () => {
  const classes = useStyles();
  return (
    <div style={{ minHeight: '120vh' }}>
      <Grid container xs={12} className={classes.backgroundFix} />
      <Grid xs={10} sm={8} className={classes.gridCenterContainer}>
        <h1>Privacy And Policy</h1>
        <Box style={{ marginBottom: 30 }}>
          <Typography className={classes.text}>
            These terms and conditions outline the rules and regulations for the use of Company
            Name's Website, located at <a href="#">smileland.com</a>.
          </Typography>
        </Box>
        <Box style={{ marginBottom: 30 }}>
          <Typography className={classes.title}>License</Typography>
          <Typography className={classes.text}>
            Unless otherwise stated, Company Name and/or its licensors own the intellectual
            restrictions set in these terms and conditions.
          </Typography>
          <Typography className={classes.text}>You must not:</Typography>
          <ul className={classes.list}>
            <li>Republish material from SmileLand</li>
            <li>Sell, rent or sub-license material from SmileLand</li>
            <li>Reproduce, duplicate or copy material from SmileLand</li>
          </ul>
        </Box>
      </Grid>
    </div>
  );
};

export default About1;
