import React, { useState, useEffect } from 'react';
import { Box, Stack, Grid, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { NavLink, useLocation, useNavigate, Link } from 'react-router-dom';
import { Search, Twitter, Instagram, Facebook} from '@material-ui/icons';
import { footerData } from '../../DATA';
import './Footer.css'

const useStyles = makeStyles((theme) => ({
  Copyright: {
    backgroundColor: '#000',
    height: '70px',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    '& h6': {
      fontSize: '13px',
      color: 'grey'
    }
  },


  LinkContainer: {
    cursor: 'pointer',
    width: 'max-content',
    marginTop: '20px',
    '&:hover': {
      '& $Link': {
        color: '#007fbf',
        "&:after" :{ 
          backgroundColor: '#007fbf'
        }
      }
    }
  },
  Link: {
    fontSize: '15px',
    color: 'white',
    textDecoration: 'none',
    position: "relative",
    transition:" 0.3s",
    "&:after" :{  
      position: 'absolute',
      content: '""',
      display:"block",
      left: 0,
      bottom: "-6px",
      width: "70%",
      height: "1px",
      backgroundColor: 'white',
      transition:" 0.3s"
    },
    /*"&:hover":{ 
      color: 'grey',
      "&:after":{ 
        background: 'grey',
      }
    }*/
  },

  IconContainer: {     
    cursor: "pointer",
    marginRight: 15
  },
  Icon: { 
  fontSize: 20,    
  color: "red",
  "&:hover":{
      color: "green",
    },
  },
}));

export default function Footer() {
  const classes = useStyles();
  return (
    <>
      <Grid xs={12} container className="footerContainer">
        <Grid item xs={12} md={3} sm={12} className="logoContainer">
          <h1>shoptemplate</h1>
        </Grid>
        {footerData.map((row,key) => (
          <Grid key={key} item xs={12} sm={6} md={3} className="itemFooter">
            <h2>{row.title}</h2>
            {key != 2 ? (
              row.children.map((index) => (
                <div className="linkFContainer">
                  <Link to={index.path} className="linkF">
                    {index.title}
                  </Link>
                </div>
              ))
              ) : (
              <div className="iconFContainer">
                <a className="facebook">
                  <Facebook className="iconF" />
                </a>
                <a className="instagram">
                  <Instagram className="iconF" />
                </a>
                <a className="iconF twitter">
                  <Twitter className="iconF twitter" />
                </a>        
              </div>
            )}
          </Grid>
        ))}
      </Grid>
      <Grid container xs={12} sm={12} className={classes.Copyright}>
        <h6>Â© 2022 Nicepage Free Website Builder Software - All Rights Reserved</h6>
      </Grid>
    </>
  );
}
