import React from 'react'
import { Grid, Container, Box } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles';
import m from "../../images/category/men11.jpg";
import w from "../../images/category/women11.jpg";
import { Link } from 'react-router-dom';
import './Category.css';

const useStyles = makeStyles((theme) => ({



    button: {    
        transition: "all 0.3s ease 0s",
        color:theme.palette.fontColor.primary,
        position: "relative",
        cursor: "pointer",
        "&:after" :{  
        position: 'absolute',
        content: '""',
        display:"block",
        left: 0,
        top: "20px",
        right: "50%",
        bottom: "0",
        width: "0px",
        height: "2px",
        background: theme.palette.fontColor.primary,
        transition:" 0.3s ease 0s, left 0.3s ease 0s",
         },
        "&:hover:after":{ 
                width: "50%",
                left: "0"
            },
        },

}))

const categories =[
    {
      name: 'clothes',
      url: '/shop/clothes'
    },
    {
      name: 'shoes',
      url: '/shop/shoes'
    },
    {
      name: 'electronics',
      url: '/shop/electronics'
    }
];

const Category = () => {
  const classes = useStyles();
  return (
    <Grid id="sh" container className="categorySectionContainer">
      {categories.map((index) => (
        <div className="categoryItem">
          <img src={m}/>
          <div className="overlay">
            <Link to={index.url} style={{textDecoration: 'none'}}>
              <div>
                <h1>{index.name}</h1>
              </div>
            </Link>
          </div>
      </div> 
    ))}
    </Grid>
  )
}

