import React,{useState, useEffect} from 'react'
import {Link} from "react-router-dom"
import axios from "axios"
import {makeStyles } from '@material-ui/core/styles';
import { Grid,Button, Container} from '@material-ui/core';
import {useNavigate} from 'react-router-dom';
import Product from './Product'

  const useStyles = makeStyles({
    gridContainer: {   
        //padding: "0px 3%",
        display: "flex",
        justifyContent: "center",
        marginBottom: '150px',
        width: "100%",
        flexWr

    },
    seeMoreButton: {
        position: 'relative',
        padding: "20px",
        backgroundColor: 'white',
        color: 'black',
        border: '1px solid black',
        fontSize: '14px',
        textTransform: 'capitalize',
        fontWeight: 'bold',
        cursor: 'pointer',
        display: 'inline-block',
        alignItems: 'center',
        zIndex: 1,       
        '& $span':{
          marginRight: '10px',
        },
        "&:after":{ 
          content: '""',
          display:"block",
          position: 'absolute',
          left: 0,
          top: 0,
          width: '0%',
          height: '101%',
          backgroundColor: 'black',
          borderRadius: 0,
          transition:"0.3s ease 0s, left 0.3s ease 0s",
      },
      "&:hover:after":{ 
        width: "100%",
        left: "0",
        zIndex:-1,
      },
      "&:hover":{ 
        color:'white',
      },
    },

    "@keyframes lefttoright": {
      from: { background:'rgba(0, 0, 0, 0.2)' },
      to: { background: "none" }
  }

  });


const ProductList = () => {
    const navigate = useNavigate();
    const classes = useStyles();
    const [products,setProducts] = useState([])

    const showProducts = () => {
        axios.get("https://fakestoreapi.com/products?limit=8")
        .then((response)=>{
          console.log('product',response.data)
          setProducts(response.data)
        })
        .catch((err)=>{
          console.log("err", err)
      })
    }

useEffect(() => {
    showProducts();
  }, []);


    return (
<Container>
      <div className={classes.gridContainer}>
        {
          products.map((index,i)=>{
            if(index.category)
              {
                return(
                  <Product 
                    id={index.id} 
                    title={index.title} 
                    image={index.image} 
                    price={index.price}  
                    category={index.category}
                  />
                    )     
                }
             })
            }
            </div>
          <Grid item sm={12} xs={12} style={{marginTop: 50,display: 'flex',justifyContent:'center', alignItems:'center'}}>
            <button className={classes.seeMoreButton}>
              <span>See more products</span>
              <i class="fa-solid fa-angles-right"></i>
            </button>
          </Grid>
</Container>
    );
}

export default ProductList
