import {Link} from "react-router-dom"
import { makeStyles } from '@material-ui/core/styles';
import { Grid, Typography, Box } from '@material-ui/core';
import Rating from '@mui/material/Rating';
import { useCart } from 'react-use-cart';
import {useNavigate} from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
    GridItem: {    
        backgroundColor: 'white',
        padding: '20px',
        width: "200px", //** */
        height: "200px",//** */
        position: 'relative',
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        margin: 10,
        '&:hover': {
          '& $center': {
            opacity: 1
          }
        }
    },
    image: {
        height: '100%',
        width: '100%',
        objectFit: 'contain',
        transition: 'all 2s',

    },
    overlay: {
        position: 'absolute',
        top: 0,
        left: 0,
        boxSizing: 'border-box',
        width: '100%',
        height: '100%',
        background : 'rgba(0, 0, 0, 0.1)',    
        display:"flex",
        justifyContent:"center",
        alignItems:"center",
        borderRadius: 0,
        '&:hover': {
            //animation: "$lefttoright 1s forwards",
        }
      },
    center : {
        position: 'absolute',
        bottom: 0,
        boxSizing: 'border-box',
        height:"100%",
        width:"100%",
        margin: '0 auto',
        padding: 10,
        background: 'rgba(0, 0, 0, 0.2)',
        borderRadius: 0,
        display:"flex",
        flexDirection:"column",
        alignItems:"center",
        justifyContent: 'space-between',
        transition: 'all .5s',
        opacity: 0
    },
    top : {
        width:"100%",
        boxSizing: 'border-box',
        display:"flex",
        alignItems:"center",
    },
    bottom : {   
    },
    text: {
        fontFamily: 'Raleway, sans-serif',
        fontSize: '14px',
        color: 'white',
    },
    button: {
        transition: "all 0.3s ease",
        cursor: 'pointer',
        backgroundColor: 'black',
        display: 'block',
        justifyContent: 'center',
        alignItems: 'center',
        padding: 10,
        width: '100%',
        fontFamily: 'Montserrat, sans-serif',
        letterSpacing: '.1em',
        color: 'white',
        fontSize: '12px',
        textTransform: 'uppercase',
        outline: 'none',
        border: 'none',
        '&:hover': {
          color: 'white',
          backgroundColor: '#007fbf',
        }
        },

}))

const Product = (props) => {
    const navigate = useNavigate();
    const classes = useStyles();
    const { addItem } = useCart();
    return (
        <div onClick={()=> navigate(`/detail/${props.id}`)}  className={classes.GridItem}>   
              <img  src={props.image}  className={classes.image} />
              <Box className={classes.overlay} />
               <Box className={classes.center}>
                 <Box className={classes.top}>
                   <Rating 
                     style={{ flex: 1, fontSize: 14}}
                     name="half-rating" defaultValue={props.rating.rate} precision={0.5} readOnly />
                   <Typography className={classes.text}>${props.price}</Typography>
                 </Box>
                 <button
                   className={classes.button}
                   onClick={()=>addItem(oneproduct)}
                 >
                  add to cart
                </button>
               </Box>
        </div>
    )
}

export default Product

