import React,{useState, useEffect} from 'react'
import axios from "axios"
import {makeStyles } from '@material-ui/core/styles';
import { Grid,Button, Container} from '@material-ui/core';
import {useNavigate} from 'react-router-dom';
import Product from './Product'
import SeeMoreButton from '../SeeMoreButton';
import './productList.css';

const ProductList = () => {
  const navigate = useNavigate();
  const [products,setProducts] = useState([])

 /* const showProducts = () => {
    axios.get("https://fakestoreapi.com/products?limit=8")
    .then((response)=>{
      console.log('product',response.data)
      setProducts(response.data)
    })
    .catch((err)=>{
      console.log("err", err)
    })
  }*/

  const showProducts = () => {
    const rows = []
    axios.get("https://api.escuelajs.co/api/v1/products?offset=0&limit=20")
    .then((response)=>{
      let i = 0;
      while (rows.length < 8 ){
        if ((response.data[i].category.name !== 'Furniture') && (response.data[i].category.name !== 'Others')) {
          rows.push(response.data[i]);
        }
        i++;
      }
      setProducts(rows)
      console.log('product',products)
    })
    .catch((err)=>{
      console.log("err", err)
    })
  }

  useEffect(() => {
    showProducts();
  }, []);

  return (
    <Grid className="listProductContainer">
      <Grid container xs={12} className="list">
        {products.map((index,i)=>{
          return(
              <Product 
              id={index.id} 
              title={index.title} 
              image={index.images[0]} 
              price={index.price}  
              category={index.category.name}
              rating="4"
              item={index}
              isSold={index.id === 2 && true}
            />


          )     
        })}
      </Grid>
      <Grid container xs={12} className="list bottomButton" >
        <SeeMoreButton onClick={()=> navigate('/shop')} />
      </Grid>
    </Grid>
  );
}

export default ProductList


