import React, { useState, useEffect } from 'react';
import { Box, Stack, Grid, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { NavLink, useLocation, useNavigate, Link } from 'react-router-dom';
import { footerData } from '../DATA';

const useStyles = makeStyles((theme) => ({
  Copyright: {
    backgroundColor: '#e3cfcf',
    height: '70px',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    '& h6': {
      fontSize: '13px',
      color: 'grey'
    }
  },
  GridFooter: {
    backgroundColor: '#F4E4E4',
    marginTop: '70px',
    padding: '2% 3%',
    display: 'flex',
    justifyContent: 'center'
  },
  GridItemFooter: {
    padding: '15px',
    '& h6': {
      fontWeight: 600,
      color: 'black',
      textTransform: 'uppercase',
      lineHeight: '54px',
      textDecoration: 'none',
      fontSize: '18px',
    }
  },
  Link: {
    fontSize: '15px',
    color: 'white',
    textDecoration: 'none',
    position: "relative",
    cursor: "pointer",
      "&:after" :{  
        position: 'absolute',
        content: '""',
        display:"block",
        left: 0,
        bottom: "-6px",
        width: "70%",
        height: "1px",
        background: 'white',
        transition:" 0.3s ease 0s, left 0.3s ease 0s",
      },
        "&:hover:after":{ 
          width: "50%",
          left: "0"
          },
  },
  LogoContainer: {
    backgroundColor: '#F4E4E4',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  },
  Logo: {
    color: '#F78181',
    fontSize: '2rem',
    fontWeight: 700
  },
  Icon: {
    color: 'black',
    '&:hover': {
      color: 'green'
    }
  }
}));

export default function Footer() {
  const classes = useStyles();

  return (
    <>
      <Grid xs={12} container className={classes.GridFooter}>
        <Grid item xs={12} md={3} sm={12} className={classes.LogoContainer}>
          <Typography className={classes.Logo}>fakestore</Typography>
        </Grid>
        {footerData.map((row) => (
          <Grid item xs={12} sm={6} md={3} className={classes.GridItemFooter}>
            <Box  style={{ marginTop: '20px'}}>
              <h6>{row.title}</h6>
            </Box>
            {row.children.map((index) => (
              <Box style={{ marginTop: '20px'}}>
                <Link to={index.path} className={classes.Link}>
                  {index.title}
                </Link>
              </Box>
            ))}
          </Grid>
        ))}
      </Grid>
      <Grid container xs={12} sm={12} className={classes.Copyright}>
        <h6>Â© 2022 Nicepage Free Website Builder Software - All Rights Reserved</h6>
      </Grid>
    </>
  );
}
