import React,{useState, useEffect} from 'react';
import { Grid,Button, Typography,Box,Container, TextField} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import {useNavigate,useParams,Link} from 'react-router-dom';
import axios from "axios";
import Close from '@material-ui/icons/Close';
import { useCart } from 'react-use-cart';

const useStyles = makeStyles((theme) => ({
    ProductDetailContainer: {
        backgroundColor: 'white',
        marginTop: '185px',
        ['@media (max-width:780px)']: { 
          marginTop: '130px',
        }
      },
      ProductDetailHeader: {
        backgroundColor: 'rgb(218, 209, 209)',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        height: '130px'
      },
      Title: {
        fontWeight: 'bold',
        color: 'black',
        fontSize: '2rem',
        padding: '0px',
        margin: '0px',
        marginSottom: '12px'
      },
      grid:{
        marginBottom: '20px'
      },
      MenuDetailHeader:{
        display: 'flex',
        alignItems: 'center',
        '& $hr':{
          borderLeft:'1px solid black',
          height: '15px',
        }
      },
      Link:{
        color:'black',
        margin: '10px',
        textDecoration: 'none'
      },
      container: {
        backgroundColor:'red',
        marginTop: '50px',
        paddingBottom: 100,
        minHeight: 500
      },
      formContainer: {
        backgroundColor: 'pink',
        display: 'flex',
        position: 'relative',
        width: '100%',
        height: 400
      },
      login: {
        backgroundColor:'white',
        position: 'absolute',
        left: 0,
        top: 0,
        width: '70%',
        height: '100%',
        display: 'flex',
        justifyContent: 'center',
        ['@media (max-width:780px)']: {
          width: '100%',
        },
      },
      banner: {
        backgroundColor: 'rgba(0, 0, 0, .5)',
        backgroundAttachment: 'fixed',
        position: 'absolute',
        top: 0,
        width: '30%',
        height: '100%',
        right:  0, 
        ['@media (max-width:780px)']: {
            display: 'none'
        },
      },
      bannerMobile: {
        backgroundColor:'lightgray',
        marginTop: 30,
        width: '100%',
        display: 'none',
        ['@media (max-width:780px)']: {
            display: 'flex',
            justifyContent: 'center'
        },
      },
      inputGroup: {
        backgroundColor:'green',
        marginTop: 70,
        width: '300px',
        display: 'flex',
        flexDirection: 'column',
        ['@media (max-width:780px)']: {
           width: '100%'
        },
      },
      input: {
        width: '100%',
        margin: '10px 0px',
      },
      submitButton: {
        cursor: 'pointer',
        display: 'block',
        backgroundColor: 'blue',
        width: '70%',
        height: 50,
        padding: '5px',
        borderRadius: '25px',
        color: 'white',
        fontSize: '14px',
        border: 'none',  
        outline: 'none',     
        margin: '10px auto',
        '&:focus': {
          outline: 'none'
        },
      },
      checkbox: {

      }
}))

const LoginSignup = () => {
    const classes = useStyles();
    const breakpoint = 780;

    const [login, setLogin] = useState(true);
    const [width, setWidth] = React.useState(window.innerWidth);

    const submit = () => {
      };

    React.useEffect(() => {
     const handleResizeWindow = () => setWidth(window.innerWidth);
      window.addEventListener("resize", handleResizeWindow);
      return () => {
        window.removeEventListener("resize", handleResizeWindow);
      };
    }, []);

    const handleChangeForm = () => {
      setLogin(!login);
    };

    return (
        <Grid container  className={classes.ProductDetailContainer}>
          <Grid item  xs={12} sm={12}  className={classes.ProductDetailHeader}>   
            <h1  className={classes.Title}>user</h1>
            <Box  className={classes.MenuDetailHeader}>
              <Link to='/' className={classes.Link}>Home</Link>
              <hr/>
              <Link to='#' className={classes.Link}>user</Link>
            </Box>
         </Grid>
         <Container maxWidth="md" className={classes.container}>
         <Grid container>
           <Grid xs={12} className={classes.formContainer}>
             <div item 
               className={classes.login}
               style={{
                left: (login && width > breakpoint) && '100%',
                transform: (login && width > breakpoint) && 'translate(-100%, 0)',
                transition: '.7s ease-out'
               }}
             >
              <form className={classes.inputGroup}>
                <TextField
                  name="fullname"
                  label="Username"
                  type="text"
                  className={classes.input}
                  InputProps={{
                    className: classes.input,
                  }}
                />
                <TextField
                  name="email"
                  label="Email"
                  type="email"
                  className={classes.input}
                  InputProps={{
                    className: classes.input,
                  }}
                />
                <TextField
                  name="password"
                  label="Password"
                  type="password"
                  className={classes.input}
                  InputProps={{
                    className: classes.input,
                  }}
                />
                <input className={classes.checkbox} type="checkbox" />
                <button className={classes.submitButton} onClick={submit}>
                  {login? 'Sign Up' : 'Login' }
                </button>
              </form>
               <div  className={classes.bannerMobile}>
                 <h3>{login? "Don't have an account?" : "Alredy have an account?" }</h3>
                 <button onClick={handleChangeForm}>{login? 'Sign Up' : 'Login' }</button>
             </div>   
             </div>
             <div item
               className={classes.banner}
               style={{
                right: (login && width > breakpoint) && '100%',
                transform: (login && width > breakpoint) && 'translate(100%, 0)',
                transition: '.7s ease-out'
               }}
            >
               <h3>{login? "Don't have an account?" : "Alredy have an account?" }</h3>
               <button onClick={handleChangeForm}>{login? 'Sign Up' : 'Login' }</button>
             </div>   
           </Grid>
           </Grid>
         </Container>
      </Grid>
    )
}

export default LoginSignup
