import React,{useState, useEffect,useRef} from 'react'
import { Link, useNavigate } from "react-router-dom"
import axios from "axios"
import { Grid,Button, Typography,Box,Container} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { LocationOn, Style } from '@material-ui/icons';
import Carousel from 'react-material-ui-carousel'
import Slider from "react-slick";
import Product from './Product';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './slick.css';
import { ArrowRightAlt } from '@material-ui/icons';
import SeeMoreButton from '../SeeMoreButton';

const useStyles = makeStyles((theme) => ({
    PromotionContainer: {
      margin: '0px auto',
    },
    buttonContainer: {
      display:'flex',
      justifyContent:'center',   
      alignItems:'center',
      padding: 10
    },

    PromotionItem: {
    },
    carousel: {
    },
    containerItem: {
        position: 'relative', 
        display: "flex",
        justifyContent:'center',   
        alignItems:'center',  
    },
    imageItem: {
        minWidth:'250px',
        minHeight:'300px',
        width: '100%',
        height: '500px',
        objectFit:"cover"    
    },
    overlay: {
        bottom: 0,
        margin: '0px auto',   
        position: 'absolute',
        width: '70%',
        height: '20%',
        background : 'rgba(0, 0, 0, 0.1)',
        borderTopLeftRadius: 10,
        borderTopRightRadius: 10,    
        display:"flex",
        justifyContent:"center",
        alignItems:"center",
        animation: "$animWidth 2s forwards"
      },
    priceItem: {
        fontSize: '20px',
        color: 'white',
        textDecoration: 'none'
    },
    containerTitlePromotion: {
      //backgroundColor: 'red',
      padding: 20,
    },
    titlePromotion: {
        fontSize: '40px',
        fontFamily: 'Raleway, sans-serif',
        fontWeight: 500,
        color: '#333333',
    },
    descriptionPromotion: {
        marginTop: 20,
        fontFamily: 'Raleway, sans-serif',
        lineHeight: '35px',
        fontSize: '18px',
        color: 'grey',
    },
    buttonItem: {    
        transition: "all 0.3s ease 0s",
        cursor: 'pointer',
        backgroundColor: 'black',
        display: 'block',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '5px 20px',
        textDecoration: 'none',
        '& p': {
            color: 'white',
            fontSize: '8px',
            fontWeight:'bold'
          },
        '&:hover': {
          color: 'white',
          backgroundColor: '#007fbf',
        }
    },
    "@keyframes animWidth": {
        from: { height:'20%' },
        to: { height: '50%' }
    }
}));

export default function SlickProduct() {
  const classes = useStyles();
  const [products,setProducts] = useState([]);
  const [sliderRef, setSliderRef] = useState([]);

  const PreviousArrow = ({ onClick }) => (
    <button className="buttonItem prev" onClick={onClick}>
      <ArrowRightAlt style={{transform: 'rotate(180deg)'}} />
    </button>
);

  const NextArrow = ({ onClick }) => (
    <button className="buttonItem" onClick={onClick}>
      <ArrowRightAlt />
    </button>
  );


  const showProducts = () => {
    axios.get("https://fakestoreapi.com/products?limit=8")
    .then((response)=>{
      console.log('product',response.data)
      setProducts(response.data)
    })
    .catch((err)=>{
      console.log("err", err)
    })
  }
useEffect(() => {
  showProducts();
}, []);

  return (
    <Grid xs={12}  className={classes.PromotionContainer}>
      <div className={classes.buttonContainer}>
        <div className={classes.arrowContainer}>
          <PreviousArrow onClick={sliderRef?.slickPrev} />
          <NextArrow onClick={sliderRef?.slickNext} />
        </div>
        <SeeMoreButton />
      </div>
        <Slider 
          ref={setSliderRef}
          autoplay={false}
          dots
          infinite={true}
          speed={500}
          arrows={false}
          slidesToShow= {1}
          slidesToScroll= {1}
          variableWidth= {true}
          className={classes.carousel}
        >
          {products.map((index,i)=>{
            return(
              <Product 
                id={index.id} 
                title={index.title} 
                image={index.image} 
                price={index.price}  
                category={index.category}
                rating={index.rating}
              />
            )         
          })}
        </Slider>
    </Grid>
  );
}
