import React,{useState, useEffect} from 'react'
import { Grid,Button} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import {useNavigate,useParams} from 'react-router-dom';
import axios from "axios"

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
        marginBottom: theme.spacing(2),
         width: '100%',
    }
    },
  }));


const ProductDetail = () => {
    const classes = useStyles();
    let {id} = useParams()
    const [oneproduct,setOneprodut]= useState([])
    const navigate = useNavigate();

  const getSingle = (userid) => {
  axios.get(`https://fakestoreapi.com/products/${userid}`)
  .then((res)=>{
      console.log('data',res.data)
      setOneprodut(res.data)
  })
  .catch((error)=> console.log(error))
 }

useEffect(() => {
    if(id && id !== ""){
      getSingle(id)
    }
 },[id,oneproduct]);

    return (
        <Grid container style={{backgroundColor: 'red'}}>
          <Grid item xs={12} sm={8} style={{padding: "0px 4%",backgroundColor: 'green'}}>

            <div>PRODUCT DETAIL</div>
        {oneproduct.length === 0 ? (
     <h1>is loading ....</h1>
        ) : (    
            <div>
                <Button style={{marginBottom:"20px"}} onClick={()=>  navigate("/")}  variant="contained" color="primary">GO BACK</Button>
                <div>
                    <img src={oneproduct.image}  style={{maxWidth:"100px",maxHeight:"100px"}}/>
                    <h1>title : </h1>{oneproduct.title}
                    <h1>price : </h1>{oneproduct.price}
                    <h1>category : </h1>{oneproduct.category}
                </div>

                </div> 
                  )}

            </Grid>
        </Grid>
)}

export default ProductDetail
