const Cart = () => {
    const classes = useStyles();
    const { setPath } = useContext(AuthContext);
    const {
      isEmpty,
      totalUniqueItems,
      cartTotal,
      totalItems,
      items,
      updateItemQuantity,
      removeItem,
      emptyCart
    } = useCart();

    const handleChange = (item) => event => {

      updateItemQuantity(item.id, event.target.valueAsNumber);
    };

    useEffect(() => {
      setPath('cart');
    }, []);


  if(isEmpty) {
    return (
      <h1>Your cart is Empty....</h1>
    )
  }

  return (
    <Grid id="cart">
      <HeaderTitle />
      <Container className={classes.container}>
        <Grid container justifyContent='space-between' alignItems="stretch">
          <Grid container  item xs={12} md={7} className={classes.gridContainer}>
            <Grid item container xs={12} sm={12} className={classes.HeaderTable}>
              <Grid item xs={12} sm={5} className={classes.HeaderItem}>
                <Typography className={classes.TableHeaderTitle}>PRODUCT</Typography>
              </Grid>
              <Grid item xs={12} sm={2} className={classes.HeaderItem}>
                <Typography className={classes.TableHeaderTitle}>PRICE</Typography>
              </Grid>
              <Grid item xs={12} sm={3} className={classes.HeaderItem}>
                <Typography className={classes.TableHeaderTitle}>QUANTITY</Typography>	
              </Grid>
              <Grid item xs={12} sm={2} className={classes.HeaderItem}>
                <Typography className={classes.TableHeaderTitle}>SUBTOTAL</Typography>
              </Grid>    
            </Grid>
            {items.map((item,index)=>{
              return(
              <>
                <Grid key={index} container xs={12} sm={12} className={classes.HeaderTable}>
                  <Close className={classes.RemoveIcon} onClick={()=> removeItem(item.id)}/>
                  <Grid item xs={12} sm={5} className={classes.HeaderItem}>
                    <div className={classes.containerProduct}>
                      <div className={classes.containerImage}>
                        <img src={item.images[0]} className={classes.image}/>
                      </div>
                      <div className={classes.containerDescription}>
                        <Typography className={classes.titleProduct}>{item.title}</Typography>
                        <Typography className={classes.descriptionProduct}>Size: Small</Typography>
                      </div>
                    </div>
                  </Grid>
                  <Grid item xs={12} sm={2} className={classes.HeaderItem}>
                    <Typography>${item.price}</Typography>
                  </Grid>
                  <Grid item xs={12} sm={3} className={classes.HeaderItem}>
                    <div className="quantityControl">
                      <button className="left" onClick={()=>updateItemQuantity(item.id, item.quantity - 1)}>-</button>
                      <input
                        type="number"
                        value={item.quantity}
                        onChange={handleChange(item)}
                      />
                      <button className="right" onClick={()=>updateItemQuantity(item.id, item.quantity + 1)}>+</button>
                    </div>  
                 </Grid>
                 <Grid item xs={12} sm={2} className={classes.HeaderItem}>
                   <Typography>${(item.price * item.quantity).toFixed(2)}</Typography>
                 </Grid>
               </Grid> 
              </>
              )})}

              <Grid item xs={12} sm={12} className={classes.ContainerBottomButton}>
                <button className={classes.addCardButton} style={{margin:0}}>CONTINUE SHOPPING</button>
                <button
                  className={classes.addCardButton}
                  style={{margin:0}}
                  onClick={() => emptyCart()}
                >UPDATE SHOPPING CART</button>
              </Grid>
           </Grid>
           <Grid container  item xs={12} md={4} className={classes.cartTotal}>
             <Typography className={classes.cartTotalTitle}>CART TOTALS</Typography>
             <Box className={classes.cartTotalItem}>
               <Typography className={classes.cartTotalItemTextT}>Total Items</Typography>
               <Typography className={classes.cartTotalItemTextD}>{totalItems}</Typography>
             </Box>
             <Box className={classes.cartTotalItem}>
               <Typography className={classes.cartTotalItemTextT}>Subtotal</Typography>
               <Typography className={classes.cartTotalItemTextD}>${(cartTotal).toFixed(2)}</Typography>
             </Box>
             <Box className={classes.cartTotalItem}>
               <Typography className={classes.cartTotalItemTextT}>Delivery</Typography>
               <Typography className={classes.cartTotalItemTextD}>$110.00</Typography>
             </Box>
             <Box className={classes.cartTotalItem}>
               <Typography className={classes.cartTotalItemTextT}>Total</Typography>
               <Typography className={classes.cartTotalItemTextD}>${(cartTotal + 110.00).toFixed(2)}</Typography>
             </Box>
             <button className={classes.addCardButton}>PROCEED TO CHECKOUT</button>
             <Typography className={classes.cartTotalItemTextT} style={{marginTop: 40}}>Cards We Accepted</Typography>
             <Box className={classes.creditCardContainer}>
               <img className={classes.creditCard} src={paypal} />
               <img className={classes.creditCard} src={viza} />
               <img className={classes.creditCard} src={skrill} />
               <img className={classes.creditCard} src={mastercard} />
             </Box>
           </Grid>
           </Grid>
         </Container>
        </Grid>
)}

