import React from 'react';
import { Link } from 'react-router-dom';
import { Grid,Button, Typography,Box,Container} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { LocationOn } from '@material-ui/icons';
import Carousel from 'react-material-ui-carousel'
import i from '../images/promotion/chapeau.jpg'

const useStyles = makeStyles((theme) => ({
    containerItem: {
        position: 'relative', 
        display: "flex",
        width:'250px',
        height:'200px',
        justifyContent:'center',   
        alignItems:'center',  
        margin: 20,
    },
    imageItem: {
        width: '100%',
        height: "100%",
        objectFit:"cover",     
    },
    overlay: {
        top: 0,
        left: 0,
        position: 'absolute',
        width: '100%',
        height: '100%',
        background : 'rgba(0, 0, 0, 0.2)',    
        display:"flex",
        justifyContent:"center",
        alignItems:"center"
      },
    center : {
        background: "rgba(0, 0, 0, 0.2)",
        color:theme.palette.fontColor.primary,
        width:"100px",
        height:"50px",
        display:"flex",
        justifyContent:"center",
        alignItems:"center",
        padding: 10,
        textDecoration: 'none'
    },
    priceItem: {
        fontSize: '20px',
        color: 'white',
        textDecoration: 'none'
    },
    buttonItem: {    
        transition: "all 0.3s ease 0s",
        color:theme.palette.fontColor.primary,
        position: "relative",
        cursor: "pointer",
        "&:after" :{  
        position: 'absolute',
        content: '""',
        display:"block",
        left: 0,
        top: "20px",
        right: "50%",
        bottom: "0",
        width: "0px",
        height: "2px",
        background: theme.palette.fontColor.primary,
        transition:" 0.3s ease 0s, left 0.3s ease 0s",
         },
        "&:hover:after":{ 
                width: "50%",
                left: "0"
            },
        },
}));

const items = [
    {
        price: '300',
        imageUrl: '../images/promotion/chapeau.jpg',
    },
    {
        price: '400',
        imageUrl: '../images/promotion/chapeau.jpg',
    },
    {
        price: '500',
        imageUrl: '../images/promotion/chapeau.jpg',
    },
    {
        price: '600',
        imageUrl: '../images/promotion/chapeau.jpg',
    }
];

const Item = (props) => {
  const classes = useStyles();
  return (
      <Box className={classes.containerItem}>
        <img src={i} className={classes.imageItem}/>
        <div className={classes.overlay}>
          <h1 className={classes.priceItem}>{props.item.price}</h1>
          <Link to="/shop" className={classes.buttonItem}>
            <h1>Shop Now</h1>
          </Link>
        </div>
      </Box> 
    );
}

export default function PromotionSection() {
    const classes = useStyles();

  return (
    <div style={{ height: '40vh', width: '100%', backgroundColor: 'yellow' }}>
      <Carousel>
            {
                items.map( (item, i) => <Item key={i} item={item} /> )
            }
        </Carousel>
    </div>
  );
}
