import React,{useState, useEffect} from 'react'
import axios from "axios"
import {makeStyles } from '@material-ui/core/styles';
import { Grid,Button, Container} from '@material-ui/core';
import {useNavigate} from 'react-router-dom';
import Product from './Product'
import SeeMoreButton from '../SeeMoreButton';
import './productList.css';

const ProductList = () => {
  const navigate = useNavigate();
  const [products,setProducts] = useState([])

  const showProducts = () => {
    axios.get("https://fakestoreapi.com/products?limit=8")
    .then((response)=>{
      console.log('product',response.data)
      setProducts(response.data)
    })
    .catch((err)=>{
      console.log("err", err)
    })
  }

  useEffect(() => {
    showProducts();
  }, []);

  return (
    <>
    <Grid container className="listProductContainer">
      {products.map((index,i)=>{
        return(
          <Product 
            id={index.id} 
            title={index.title} 
            image={index.image} 
            price={index.price}  
            category={index.category}
            rating={index.rating}
            item={index}
            isSold={index.id === 2 && true}
          />
        )     
      })}
    </Grid>
    <Grid item sm={12} xs={12} style={{marginTop: 50,display: 'flex',justifyContent:'center', alignItems:'center'}}>
    <SeeMoreButton onClick={()=> navigate('/shop')} />
  </Grid>
  </>
  );
}

export default ProductList
