import React,{useState, useEffect} from 'react';
import { Grid,Button, Typography,Box,Container} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import {useNavigate,useParams,Link} from 'react-router-dom';
import axios from "axios";
import Product from '../components/Product/Product';
import Pagination from '../components/Pagination';
import w from '../images/category/men11.jpg';
import paypal from '../images/creditCard/paypal22.png';
import amex from '../images/creditCard/amex1.png';
import viza from '../images/creditCard/visa-credit-card.png';
import skrill from '../images/creditCard/skrill.png';
import mastercard from '../images/creditCard/mastercard11.png';
import Close from '@material-ui/icons/Close';
import { useCart } from 'react-use-cart';

const useStyles = makeStyles((theme) => ({
    ProductDetailContainer: {
        backgroundColor: 'white',
        marginTop: '185px',
        ['@media (max-width:780px)']: { 
          marginTop: '130px',
        }
      },
      ProductDetailHeader: {
        backgroundColor: 'rgb(218, 209, 209)',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        height: '130px'
      },
      Title: {
        fontWeight: 'bold',
        color: 'black',
        fontSize: '2rem',
        padding: '0px',
        margin: '0px',
        marginSottom: '12px'
      },
      grid:{
        marginBottom: '20px'
      },
      MenuDetailHeader:{
        display: 'flex',
        alignItems: 'center',
        '& $hr':{
          borderLeft:'1px solid black',
          height: '15px',
        }
      },
      Link:{
        color:'black',
        margin: '10px',
        textDecoration: 'none'
      },
      container: {
        backgroundColor:'red',
        marginTop: '50px',

      },
      formContainer: {
        backgroundColor: 'pink',
        display: 'flex',
        position: 'relative'
      },
      login: {
        backgroundColor:'blue',
        position: 'absolute',
        left: 0,
        top: 0,
        bottom: 0,
        width: '70%',
        height: 400
      },
      banner: {
        backgroundColor:'lightgray',
        position: 'absolute',
        right: 0,
        top: 0,
        width: '30%',
      },
}))

const LoginSignup = () => {
    const classes = useStyles();
    const [login, setLogin] = useState(true);

    const handleChangeForm = () => {
      setLogin(!login);
    };

    return (
        <Grid container  className={classes.ProductDetailContainer}>
          <Grid item  xs={12} sm={12}  className={classes.ProductDetailHeader}>   
            <h1  className={classes.Title}>user</h1>
            <Box  className={classes.MenuDetailHeader}>
              <Link to='/' className={classes.Link}>Home</Link>
              <hr/>
              <Link to='#' className={classes.Link}>user</Link>
            </Box>
         </Grid>
         <Container maxWidth="md" className={classes.container}>
           <Grid  xs={12} className={classes.formContainer}>
             <div className={classes.login}>
               <h1>{login? 'Login Form' : 'Sign Up Form' }</h1>
             </div>
             <div className={classes.banner}>
               <h3>{login? "Don't have an account?" : "Alredy hae an account?" }</h3>
               <button onClick={handleChangeForm}>{login? 'Sign Up' : 'Login' }</button>
             </div>   
           </Grid>
         </Container>
      </Grid>
    )
}

export default LoginSignup
