import React from 'react'
import { Grid } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles';
import img from "../images/hero/hero17.jpg";
import ButtonArow from './ButtonArow/ButtonArow';
import { HeroData } from '../DATA';
import Button from '@material-ui/core/Button';
import KeyboardDoubleArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';

const useStyles = makeStyles((theme) => ({
    GridContainer: {
        position: 'relative',  
    },
    GridButton: {
        position: 'absolute',
        top: '72%',
        width: '100%',
        display:'flex',
        justifyContent:'center'
    },
    Button : {
        display: "flex",       
        flexDirection: 'column',
        borderRadius: '100%',
        width:'80px',
        height:'80px',
        backgroundColor:'black',
        color:'white'
    },
    banner: {
        clipPath: "polygon(100% 0, 100% 50%, 50% 70%, 0 50%, 0 0)",
        width: "100%",
        //height: "100%",
        minHeight: '900px',
        objectFit:"cover",
        backgroundPosition: 'right'
    },
    overlay: {
        top: 0,
        left: 0,
        position: 'absolute',
        width: '100%',
        height: '100%',
        display: "flex",
        justifyContent:"flex-start",
        alignItems:"center",
      },
    content: {
        marginBottom:'150px',
        height:"400px",
        padding: "0 5%",
        color: theme.palette.fontColor.primary,
        ['@media (max-width:780px)']: {        
            height:"200px",
          },
        ['@media (max-width:400px)']: {        
            height:"150px",
          }
    },
    contentText :{
        fontSize: "50px",
        color:'white',
        textTransform: "uppercase",
        fontFamily: 'Raleway, sans-serif',
        ['@media (max-width:780px)']: {        
            fontSize: "30px",
          },
        ['@media (max-width:400px)']: {        
              fontSize: "25px",
            }
    },
    contentText1 :{
        fontSize: "20px",
        textTransform: "none",
        marginBottom: "30px",
        fontFamily: 'Raleway, sans-serif',
        ['@media (max-width:780px)']: {        
            fontSize: "15px",
            marginBottom: "20px",
          },
        ['@media (max-width:400px)']: {        
            fontSize: "12px",
            marginBottom: "10px",
          }
    },
}))
const Hero = () => {
    const classes = useStyles();

    return (
         <Grid container className={classes.GridContainer}>
              <img src={img} className={classes.banner}/>
              <div className={classes.overlay}>
             {HeroData.map((item,index)=>{
                        return(
                        <div className={classes.content}>
                           <h5 className={classes.contentText1}>{item.content1}</h5>
                           <h1 className={classes.contentText}>{item.content2}</h1>
                           <h1 className={classes.contentText1}>{item.content3}</h1>
                        </div>)}
                        )} 
                        </div>
                        <div className={classes.GridButton}>
                        <Button variant="contained" className={classes.Button}>
                           Shop
                          <KeyboardDoubleArrowDownIcon />
                        </Button>
                        </div>
                        </Grid>
                        )
                    }

export default Hero
