import React from 'react';
import { Link } from 'react-router-dom';
import { Grid,Button, Typography,Box,Container} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { LocationOn, Style } from '@material-ui/icons';
import Carousel from 'react-material-ui-carousel'
import i from '../images/promotion/chapeau.jpg'

const useStyles = makeStyles((theme) => ({
    PromotionContainer: {
        backgroundColor: 'white',
        display: "flex",
        justifyContent:'center',   
        //alignItems:'center',
    },
    PromotionItem: {
        backgroundColor: 'white',
    },
    carousel: {
        width: '100%',
        height: '100%', 
    },
    containerItem: {
        position: 'relative', 
        background : 'white',
        display: "flex",
        justifyContent:'center',   
        alignItems:'center',

    },
    imageItem: {
        minWidth:'250px',
        minHeight:'200px',
        height: '350px',
        objectFit:"cover",
        '@media (max-width:350px)': {
          height:"250px",
        }     
    },
    overlay: {
        bottom: 0,
        margin: '0px auto',   
        position: 'absolute',
        width: '70%',
        height: '20%',
        background : 'rgba(0, 0, 0, 0.1)',
        borderTopLeftRadius: 10,
        borderTopRightRadius: 10,    
        display:"flex",
        justifyContent:"center",
        alignItems:"center",
        animation: "$animWidth 2s forwards"
      },
    priceItem: {
        fontSize: '20px',
        color: 'white',
        textDecoration: 'none'
    },
    containerTitlePromotion: {
      //backgroundColor: 'red',
      padding: 20,
    },
    TitlePromotion: {
        fontSize: '20px',
        color: 'black',
    },
    buttonItem: {    
        transition: "all 0.3s ease 0s",
        cursor: 'pointer',
        backgroundColor: 'black',
        display: 'block',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '5px 20px',
        textDecoration: 'none',
        '& p': {
            color: 'white',
            fontSize: '8px',
            fontWeight:'bold'
          },
        '&:hover': {
          color: 'white',
          backgroundColor: '#007fbf',
        }
    },
    "@keyframes animWidth": {
        from: { height:'20%' },
        to: { height: '50%' }
    }
}));

const items = [
    {
        price: '300',
        imageUrl: '/static/promotion/chapeau.jpg',
    },
    {
        price: '400',
        imageUrl: '/static/promotion/chapeau.jpg',
    },
    {
        price: '500',
        imageUrl: '/static/promotion/chapeau.jpg',
    },
    {
        price: '600',
        imageUrl: '/static/promotion/chapeau.jpg',
    }
];

const Item = (props) => {
  const classes = useStyles();
  return (
      <Box className={classes.containerItem}>
        <img alt="kk" src={props.item.imageUrl} className={classes.imageItem}/>
        <div className={classes.overlay}>
          <h1 className={classes.priceItem}>{props.item.price}</h1>
          <Link to="/shop" className={classes.buttonItem}>
            <p>Shop Now</p>
          </Link>
        </div>
      </Box> 
    );
}

export default function PromotionSection() {
  const classes = useStyles();
  return (
    <Container>
    <Grid xs={12} container className={classes.PromotionContainer}>
      <Grid item xs={12} md={6}className={classes.PromotionItem}>
        <Carousel
          navButtonsAlwaysVisible
          navButtonsProps={{
            style: {
                backgroundColor: 'transparent',
                color: 'black',
                width: 50,
                height: 50
            }
        }} 


          className={classes.carousel}>
          {
            items.map( (item, i) => <Item key={i} item={item} /> )
          }
        </Carousel>
      </Grid>
      <Grid item xs={12} md={6} className={classes.PromotionItem}>
        <Box className={classes.containerTitlePromotion}>
          <Typography className={classes.TitlePromotion}>Promotion</Typography>
        </Box>
      </Grid>
    </Grid>
    </Container>
  );
}
