import React,{useState, useEffect, useRef, useContext} from 'react';
import { Grid,Button, Typography,Box,Container, TextField,IconButton, InputAdornment} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import {useNavigate,useParams,Link} from 'react-router-dom';
import axios from "axios";
import SignUpForm from '../../components/Form/SignUpForm';
import LoginForm from '../../components/Form/LoginForm';
import AuthContext from '../../context/AuthContext';
import HeaderTitle from '../../components/HeaderTitle/HeaderTitle';
import './AuthPage.css';

const useStyles = makeStyles((theme) => ({

      ProductDetailHeader: {
        backgroundColor: 'rgb(218, 209, 209)',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        height: '130px'
      },
      Title: {
        fontWeight: 'bold',
        color: 'black',
        fontSize: '2rem',
        padding: '0px',
        margin: '0px',
        marginSottom: '12px'
      },
      grid:{
        marginBottom: '20px'
      },
      MenuDetailHeader:{
        display: 'flex',
        alignItems: 'center',
        '& $hr':{
          borderLeft:'1px solid black',
          height: '15px',
        }
      },
      Link:{
        color:'black',
        margin: '10px',
        textDecoration: 'none'
      },
      container: {
        //backgroundColor:'red',
        marginTop: '50px',
        paddingBottom: 100,
        minHeight: 500
      },
      formContainer: {
        //backgroundColor: 'pink',
        display: 'flex',
        position: 'relative',
        width: '100%',
        minHeight: 400,
        boxShadow:  "0 0 16px rgb(0 0 0 / 15%)" ,
      },
      login: {
        //backgroundColor:'yellow',
        position: 'relative',
        left: 0,
        top: 0,
        width: '70%',
        height: '100%',
        boxSizing: 'border-box',
        padding: 10,
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column',
        '@media (max-width:780px)': {
          position: 'relative',
          width: '100%',
        },
      },
      banner: {
        backgroundColor: 'rgba(0, 0, 0, .5)',
        backgroundAttachment: 'fixed',
        position: 'absolute',
        top: 0,
        width: '30%',
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column',
        boxSizing: 'border-box',
        padding: 10,
        height: '100%',
        right:  0, 
        '@media (max-width:780px)': {
            display: 'none'
        },
      },
      bannerMobile: {
        backgroundColor:'lightgray',
        marginTop: 30,
        width: '100%',
        display: 'none',
        '@media (max-width:780px)': {
            display: 'flex',
            justifyContent: 'center'
        },
      },
      inputGroup: {
        //backgroundColor:'green',
        marginTop: 20,
        width: '70%',
        display: 'flex',
        flexDirection: 'column',
        '@media (max-width:780px)': {
           width: '100%',
        },
      },
      input: {
        width: '100%',
        margin: '10px 0px',
      },
      submitButton: {
        cursor: 'pointer',
        display: 'block',
        backgroundColor: 'black',
        width: '70%',
        height: 40,
        padding: '5px',
        border: 'none',  
        outline: 'none',     
        margin: '30px auto',
        color: 'white',
        fontWeight: 500,
        textTransform: 'uppercase',
        letterSpacing: '.1em',
        fontFamily: 'Raleway, sans-serif',
        '&:focus': {
          outline: 'none'
        },
      },
      bannerButton: {
        cursor: 'pointer',
        display: 'block',
        backgroundColor: 'transparent',
        width: '40%',
        height: 40,
        borderRadius: 25,
        padding: '5px',
        border: '1px solid white',  
        outline: 'none',     
        margin: '30px auto',
        fontSize: '14px',
        color: 'white',
        fontWeight: 500,
        textTransform: 'uppercase',
        letterSpacing: '.1em',
        fontFamily: 'Raleway, sans-serif',
        '&:focus': {
          outline: 'none'
        },
      },
      checkbox: {
        margin: '10px 5px 10px 0px',
        accentColor: 'black',
        width: 13,
        height: 13
      },
      checkboxText: {
        fontSize: '14px'
      },
      formTitle: {
        fontSize: '24px',
        color: 'black',
        fontWeight: 500,
        textTransform: 'uppercase',
        fontFamily: 'Raleway, sans-serif',
        transition: '1.2s ease-in-out'

      },
      bannerTitle: {
        fontSize: '14px',
        color: 'white',
        fontWeight: 500,
        textTransform: 'none',
        letterSpacing: '.1em',
        fontFamily: 'Raleway, sans-serif',
      }
}))

const LoginSignup = () => {
  const classes = useStyles();
  const breakpoint = 780;
  const { setPath } = useContext(AuthContext);
  const [login, setLogin] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [width, setWidth] = useState(window.innerWidth);

  const childRef = useRef(null);
  const childRef1 = useRef(null);

  useEffect(() => {
    const handleResizeWindow = () => setWidth(window.innerWidth);
    window.addEventListener("resize", handleResizeWindow);
    return () => {
      window.removeEventListener("resize", handleResizeWindow);
    };
  }, []);

  useEffect(() => {
    setPath('user');
  }, []);

  const handleChangeForm = () => {
    setLogin(!login);
    childRef.current.reset();
    childRef1.current.reset();
  };

  return(
    <Grid id="auth">
      <HeaderTitle />
      <Container maxWidth="md" className="auth-container">
        <Grid container>
          <Grid xs={12} className="auth-gridContainer">
            <div 
              className="auth-formContainer"
              style={{
                left: (login && width > breakpoint) && '100%',
                transform: (login && width > breakpoint) && 'translate(-100%, 0)'
              }}
            >
              <h1>{login? 'Login' : 'Sign Up'}</h1>
              {login? <LoginForm ref={childRef1}/> : <SignUpForm ref={childRef}/> }   
              <div className="auth-mobile-box">
                <p>
                  {login? "Don't have an account?" : "Alredy have an account?" }
                  <span onClick={handleChangeForm}>{login? 'Sign Up' : 'Login' }</span>
                </p>
              </div>   
            </div>
            <div
              className="auth-banner"
              style={{
                right: (login && width > breakpoint) && '100%',
                transform: (login && width > breakpoint) && 'translate(100%, 0)',
              }}
             >
              <h1>
                {login? "Don't have an account?" : "Already have an account?" }
              </h1>
              <button onClick={handleChangeForm}>
                {login? 'Sign Up' : 'Login' }
              </button>
            </div>   
          </Grid>
        </Grid>
      </Container>
    </Grid>
  )
}
export default LoginSignup;
