import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Typography, Grid, Paper, Container, Box } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import m from '../images/About/about.jpg'

const useStyles = makeStyles((theme) => ({
  backgroundFix: {
    height: '300px',
    width: '100%',
    backgroundColor: 'black',
    backgroundAttachment: 'fixed',
    zIndex: 2,
    display: 'flex',
    justifyContent: 'center'
  },
  centerTitle: {
    fontSize: '37px',
    fontWeight: 500,
    color: 'white',
    marginTop: 50
  },
  gridCenterContainer: {
    backgroundColor: 'white',
    margin: '0 auto',
    marginTop: -150,
    minHeight: '400px',
    display: 'flex',
    flexDirection: 'column',
    boxShadow: '0 10px 25px 0 rgb(1 45 126 / 15%)'
  },
  text: {
    fontSize: '16px',
    fontWeight: 400,
    lineHeight: '2rem',
    color: '#4c494a',
    marginTop: 10,
    fontFamily: 'Open Sans, sans-serif',
    '& a': {
      color: '#39B3BA'
    }
  },
  title: {
    fontFamily: 'Open Sans, sans-serif',
    fontSize: '19px',
    fontWeight: 'bold',
    color: '#F78181',
    marginBottom: 10
  },
  list: {
    paddingLeft: 35,
    '& li': {
      fontSize: '16px',
      fontWeight: 400,
      lineHeight: '2rem',
      color: '#4c494a',
      marginTop: 10,
      fontFamily: 'Open Sans, sans-serif'
    }
  },
  Overlay: {
    height: '300px',
    width: '100%',
    background: 'black'
  },
  backgroundAbout: {
    width: '100%',
    height: '100%',
    objectFit:'cover'
  }
}));

const Aboutk = () => {
  const classes = useStyles();
  return (
    <div>
      <Grid container xs={12} className={classes.backgroundFix}>
        <h1 className={classes.centerTitle}>About Us</h1>
      </Grid>
      <Grid xs={10} sm={8} className={classes.gridCenterContainer}>
        <Box className={classes.Overlay}>
          <img src={m} className={classes.backgroundAbout}/>
        </Box>
        <Box style={{ marginBottom: 30 }}>
          <Typography className={classes.text}>
            These terms and conditions outline the rules and regulations for the use of Company
            Name's Website, located at <a href="#">smileland.com</a>.
          </Typography>
        </Box>
      </Grid>
    </div>
  );
};

export default Aboutk;
