import React,{useState, useEffect} from 'react';
import { Grid,Button, Typography,Box,Container, TextField} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import * as Yup from 'yup';
import { useFormik, Form, FormikProvider } from 'formik';
import {useNavigate,useParams,Link} from 'react-router-dom';
import axios from "axios";
import Close from '@material-ui/icons/Close';
import { useCart } from 'react-use-cart';

const useStyles = makeStyles((theme) => ({
    ProductDetailContainer: {
        backgroundColor: 'white',
        marginTop: '185px',
        '@media (max-width:780px)': { 
          marginTop: '130px',
        }
      },
      ProductDetailHeader: {
        backgroundColor: 'rgb(218, 209, 209)',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        height: '130px'
      },
      Title: {
        fontWeight: 'bold',
        color: 'black',
        fontSize: '2rem',
        padding: '0px',
        margin: '0px',
        marginSottom: '12px'
      },
      grid:{
        marginBottom: '20px'
      },
      MenuDetailHeader:{
        display: 'flex',
        alignItems: 'center',
        '& $hr':{
          borderLeft:'1px solid black',
          height: '15px',
        }
      },
      Link:{
        color:'black',
        margin: '10px',
        textDecoration: 'none'
      },
      container: {
        //backgroundColor:'red',
        marginTop: '50px',
        paddingBottom: 100,
        minHeight: 500
      },
      formContainer: {
        //backgroundColor: 'pink',
        display: 'flex',
        position: 'relative',
        width: '100%',
        minHeight: 400,
        boxShadow:  "0 0 16px rgb(0 0 0 / 15%)" 
      },
      login: {
        //backgroundColor:'yellow',
        position: 'relative',
        left: 0,
        top: 0,
        width: '70%',
        height: '100%',
        boxSizing: 'border-box',
        padding: 10,
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column',
        '@media (max-width:780px)': {
          position: 'relative',
          width: '100%',
        },
      },
      banner: {
        backgroundColor: 'rgba(0, 0, 0, .5)',
        backgroundAttachment: 'fixed',
        position: 'absolute',
        top: 0,
        width: '30%',
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column',
        boxSizing: 'border-box',
        padding: 10,
        height: '100%',
        right:  0, 
        '@media (max-width:780px)': {
            display: 'none'
        },
      },
      bannerMobile: {
        backgroundColor:'lightgray',
        marginTop: 30,
        width: '100%',
        display: 'none',
        '@media (max-width:780px)': {
            display: 'flex',
            justifyContent: 'center'
        },
      },
      inputGroup: {
        //backgroundColor:'green',
        marginTop: 20,
        width: '70%',
        display: 'flex',
        flexDirection: 'column',
        '@media (max-width:780px)': {
           width: '100%',
        },
      },
      input: {
        width: '100%',
        margin: '10px 0px',
      },
      submitButton: {
        cursor: 'pointer',
        display: 'block',
        backgroundColor: 'black',
        width: '70%',
        height: 40,
        padding: '5px',
        border: 'none',  
        outline: 'none',     
        margin: '30px auto',
        color: 'white',
        fontWeight: 500,
        textTransform: 'uppercase',
        letterSpacing: '.1em',
        fontFamily: 'Raleway, sans-serif',
        '&:focus': {
          outline: 'none'
        },
      },
      bannerButton: {
        cursor: 'pointer',
        display: 'block',
        backgroundColor: 'transparent',
        width: '40%',
        height: 40,
        borderRadius: 25,
        padding: '5px',
        border: '1px solid white',  
        outline: 'none',     
        margin: '30px auto',
        fontSize: '14px',
        color: 'white',
        fontWeight: 500,
        textTransform: 'uppercase',
        letterSpacing: '.1em',
        fontFamily: 'Raleway, sans-serif',
        '&:focus': {
          outline: 'none'
        },
      },
      checkbox: {
        margin: '10px 5px 10px 0px',
        accentColor: 'black',
        width: 13,
        height: 13
      },
      checkboxText: {
        fontSize: '14px'
      },
      formTitle: {
        fontSize: '24px',
        color: 'black',
        fontWeight: 500,
        textTransform: 'uppercase',
        fontFamily: 'Raleway, sans-serif',

      },
      bannerTitle: {
        fontSize: '14px',
        color: 'white',
        fontWeight: 500,
        textTransform: 'none',
        letterSpacing: '.1em',
        fontFamily: 'Raleway, sans-serif',
      }
}))

const LoginSignup = () => {
    const classes = useStyles();
    const breakpoint = 780;

    const [login, setLogin] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const [width, setWidth] = React.useState(window.innerWidth);

    const RegisterSchema = Yup.object().shape({
      username: Yup.string().min(4, 'Too Short!').max(50, 'Too Long!').required('Username required'),
      email: Yup.string().email('Email must be a valid email address').required('Email is required'),
      password: Yup.string().required('Password is required')
    });

    const formik = useFormik({
      initialValues: {
        username: '',
        email: '',
        password: ''
      },
      validationSchema: RegisterSchema,
      onSubmit: (values) => {
       alert('data', values);
      }
    });

    const { errors, touched, isSubmitting, getFieldProps, setFieldValue } = formik;

    React.useEffect(() => {
     const handleResizeWindow = () => setWidth(window.innerWidth);
      window.addEventListener("resize", handleResizeWindow);
      return () => {
        window.removeEventListener("resize", handleResizeWindow);
      };
    }, []);

    const handleChangeForm = () => {
      setLogin(!login);
    };

    return (
        <Grid container  className={classes.ProductDetailContainer}>
          <Grid item  xs={12} sm={12}  className={classes.ProductDetailHeader}>   
            <h1  className={classes.Title}>user</h1>
            <Box  className={classes.MenuDetailHeader}>
              <Link to='/' className={classes.Link}>Home</Link>
              <hr/>
              <Link to='#' className={classes.Link}>user</Link>
            </Box>
         </Grid>
         <Container maxWidth="md" className={classes.container}>
         <Grid container>
           <Grid xs={12} className={classes.formContainer}>
             <div 
               className={classes.login}
               style={{
                left: (login && width > breakpoint) && '100%',
                transform: (login && width > breakpoint) && 'translate(-100%, 0)',
                transition: '.7s ease-out'
               }}
             >
              <h1 className={classes.formTitle}>{login? 'Sign Up' : 'Login'}</h1>
              <FormikProvider value={formik}>
              <Form className={classes.inputGroup} autoComplete="off" noValidate onSubmit={formik.handleSubmit}>
                <TextField
                  name="fullname"
                  label="Username"
                  type="text"
                  className={classes.input}
                  InputProps={{
                    className: classes.input,
                  }}
                />
                <TextField
                  name="email"
                  label="Email"
                  type="email"
                  className={classes.input}
                  InputProps={{
                    className: classes.input,
                  }}
                />
                <TextField
                  name="password"
                  label="Password"
                  type="password"
                  className={classes.input}
                  InputProps={{
                    className: classes.input,
                  }}
                />
                <div>
                  <input className={classes.checkbox} type="checkbox" />
                  <span className={classes.checkboxText}>Remember Me</span>
                </div>
                <button className={classes.submitButton} onClick={ha}>
                  {login? 'Sign Up' : 'Login' }
                </button>
              </Form>
              </FormikProvider>
               <div  className={classes.bannerMobile}>
                 <h3>{login? "Don't have an account?" : "Alredy have an account?" }</h3>
                 <button onClick={handleChangeForm}>{login? 'Sign Up' : 'Login' }</button>
               </div>   
             </div>
             <div
               className={classes.banner}
               style={{
                right: (login && width > breakpoint) && '100%',
                transform: (login && width > breakpoint) && 'translate(100%, 0)',
                transition: '.7s ease-out'
               }}
             >
               <h1 className={classes.bannerTitle}>
                {!login? "Don't have an account?" : "Already have an account?" }
               </h1>
               <button className={classes.bannerButton} onClick={handleChangeForm}>
                 {!login? 'Sign Up' : 'Login' }
               </button>
             </div>   
           </Grid>
           </Grid>
         </Container>
      </Grid>
    )
}

export default LoginSignup
