import React,{useState, useEffect, useContext} from 'react';
import { Grid,Button, Typography,Box, Container} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { Twitter, Instagram, Facebook} from '@material-ui/icons';
import {useNavigate,useParams,Link} from 'react-router-dom';
import axios from "axios";
import { useCart } from 'react-use-cart';
import HeaderTitle from '../HeaderTitle/HeaderTitle';
import AuthContext from '../../context/AuthContext';
import './productDetail.css';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
        marginBottom: theme.spacing(2),
         width: '100%',
    }
    },
    grid:{
      marginBottom: '20px'
    },
    MenuDetailHeader:{
      display: 'flex',
      alignItems: 'center',
      '& $hr':{
        borderLeft:'1px solid black',
        height: '15px',
      }
    },
    Link:{
      color:'black',
      margin: '10px',
      textDecoration: 'none'
    },


    ProductDetailTextId:{
      fontSize: '1rem',
      color: 'black',
      fontWeight:'bold'
    },

    plusCard:{
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      backgroundColor: 'white',
      width: '120px',
      height: '45px',
      border: '1px solid lightgrey',
      ['@media (max-width:320px)']: {
        width:"100%",
     }
    },
    plusCardButton:{
      cursor: 'pointer',
      backgroundColor: 'white',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      width: '30px',
      height: '100%',
      color: 'black',
      fontSize: '1.2rem',
      '&:hover': {
        color: 'red'
      },
    },
    plusCardText:{
      fontSize: '1.3rem',
    },
    addCardButton:{
      cursor: 'pointer',
      backgroundColor: 'black',
      display: 'block',
      justifyContent: 'center',
      alignItems: 'center',
      flexGrow: 1,
      marginLeft: '20px',
      height: '45px',
      color: 'white',
      fontSize: '14px',
      fontWeight:'bold',
      border:'none',
      transition: '0.3s',
      '&:hover': {
        color: 'white',
        backgroundColor: '#007fbf'
      },
      '&:active': {
        color: 'white',
        backgroundColor: 'red'
      },
     ['@media (max-width:320px)']: {
        width:"100%",
        marginLeft: '0px',
        marginTop: '20px',
     }
    },
    favoritCardButton:{
      cursor: 'pointer',
      backgroundColor: 'pink',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      width: '45px',
      height: '45px',
      color: 'black',
      fontSize: '1.2rem',
      border:'none',
      marginLeft: '20px',
      '&:hover': {
        color: 'white'
      },
      ['@media (max-width:780px)']: {
        width:"100%",
        marginLeft: '0px',
        marginTop: '20px',
     }
    },
    ProductDetailTextShare:{
      fontSize: '14px',
      color: 'grey',
      fontWeight:200
    },
  }));

const color =[
    {
      id: "red"
    },
    {
      id: "grey"
    },
    {
      id: "green"
    },
    {
      id: "pink"
    }
];

const size =[
  {
    id: "s"
  },
  {
    id: "m"
  },
  {
    id: "l"
  },
  {
    id: "xl"
  }
];

const ProductDetail = () => {
  const classes = useStyles();
  const [activeColor, setActiveColor] = useState("red");
  const [activeSize, setActiveSize] = useState('s');
  const { setPath } = useContext(AuthContext);
  const {
    isEmpty,
    totalUniqueItems,
    cartTotal,
    totalItems,
    items,
    updateItemQuantity,
    removeItem,
    emptyCart,
    addItem
  } = useCart();
  let {id} = useParams()
  const [oneproduct,setOneprodut]= useState([])
  const [val, setVal] = useState(1);

  const handleChange = event => {
    setVal(event.target.value);
  };

  const getSingle = (userid) => {
    axios.get(`https://api.escuelajs.co/api/v1/products/${userid}`)
    .then((res)=>{
      console.log('data',res.data);
      setOneprodut(res.data);
    })
    .catch((error)=> console.log(error))
  }

  useEffect(() => {
    setPath('product details');
  }, []);

  useEffect(() => {
    if(id && id !== ""){
      getSingle(id);
    }
  },[id,oneproduct]);

  return (
    <Grid container className="productDetailContainer">
      <HeaderTitle />
      {oneproduct.length === 0 ? 
      ( 
        <h1>is loading ....</h1>
      ) : ( 
        <Container>
          <Grid  container className="cardProductContainer">
            <Grid item xs={12} sm={12} md={6}>  
              <div className="imageContainer">
                <img src={oneproduct.images[0]}/>
              </div>
            </Grid>
            <Grid item xs={12} sm={12} md={6} className="productDetailInfo">      
              <p className="title">{oneproduct.title}</p>
              <p className="price">${oneproduct.price}</p>
              <p className="description">{oneproduct.description}</p>
              <p className="categoryText">category: {oneproduct.category.id}</p>  
              <div className="categoryContainer color">
                <span>Color: </span>
                <div>
                  {color.map((index) => (
                    <button
                      className={activeColor === index.id ? 'activeButton' : 'deactiveButton'}
                      style={{ backgroundColor: index.id}}
                      onClick={()=> {setActiveColor(index.id)}}
                    />
                  ))}
                </div>
              </div>
              <div className="categoryContainer size">
                <span>Size: </span>
                <div>
                  {size.map((index) => (
                    <button
                      className={activeSize === index.id ? 'activeButton' : 'deactiveButton'}
                      onClick={()=> {setActiveSize(index.id)}}
                    >
                      {index.id}
                    </button> 
                  ))}
                </div>
              </div>
              <div className="buttonsContainer">
                <div className="quantityControl">
                  <button
                    className="left"
                    onClick={()=> {
                      if(val > 1){
                        setVal(val - 1)
                      }
                       console.log('vaaal --',val)
                    }}
                  >
                    -
                  </button>
                  <input type="number" onChange={handleChange} value={val}/>
                  <button
                    className="right"
                    onClick={()=> {setVal(val + 1); console.log('vaaal',val)}}
                  >
                    +
                  </button>
                </div>
                <button className={classes.addCardButton} onClick={()=>addItem(oneproduct)}>
                  ADD TO CARD
                </button>
                <button className={classes.favoritCardButton}>
                  <i class="fa-solid fa-heart-circle-plus"></i>
                </button>
              </div>
              <div className="iconContainer">
                <span className={classes.ProductDetailTextShare}>Share on: </span>
                <a href='https://fr-fr.facebook.com/' >
                  <Facebook className="icon" />
                </a>
                <a href='https://www.instagram.com/?hl=fr' >
                  <Instagram className="icon" />
                </a>
                <a href='https://twitter.com/?lang=fr' >
                  <Twitter className="icon" />
                </a>
              </div>
            </Grid>
          </Grid>
        </Container>
      )}
    </Grid>
)}

export default ProductDetail;
