import k2 from '../../images/about/about4.jpg';
import m from '../../images/about/ab.jpg';
import './BlogPage.css';
import { ourBlogData } from '../../DATA';

const useStyles = makeStyles((theme) => ({
  backgroundFixu: {
    height: '500px',
