    marginTop: -150,
    minHeight: '400px',
    display: 'flex',
    flexDirection: 'column',
    boxShadow: "0 0 16px rgb(0 0 0 / 15%)"
  },
  descriptionText: {
    padding: 30
  },
  text: {
    fontSize: '16px',
    fontWeight: 400,
    lineHeight: '2rem',
    color: '#4c494a',
    marginTop: 10,
    fontFamily: 'Noto Sans JP, sans-serif',
    '& a': {
      color: '#39B3BA'
    }
  },
  Overlay: {
    height: '200px',
    width: '100%'
