  backgroundAbout: {
    width: '100%',
    height: '100%',
    //height: "100%",
    //minHeight: '400px',
    objectFit:'cover'
  }
}));

const Blog = () => {
  return (
    <div className="containerePage">
      <div className="backgroundFeix">
        <h1>our blog</h1>
      </div>

    </div>
  );
};

export default Blog;
