import React,{useState, useEffect} from 'react';
import { Grid,Button, Typography,Box} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import {useNavigate,useParams,Link} from 'react-router-dom';
import axios from "axios";
import '../../style/ProductDetail.scss';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
        marginBottom: theme.spacing(2),
         width: '100%',
    }
    },
    grid:{
      marginBottom: '20px'
    }
  }));

const ProductDetail = () => {
    const classes = useStyles();
    let {id} = useParams()
    const [oneproduct,setOneprodut]= useState([])
    const navigate = useNavigate();

  const getSingle = (userid) => {
  axios.get(`https://fakestoreapi.com/products/${userid}`)
  .then((res)=>{
      console.log('data',res.data)
      setOneprodut(res.data)
  })
  .catch((error)=> console.log(error))
 }

useEffect(() => {
    if(id && id !== ""){
      getSingle(id)
    }
 },[id,oneproduct]);

    return (
        <Grid container className="ProductDetailContainer">
           <Grid item  xs={12} sm={12} style={{marginBottom:'50px'}} className="ProductDetailHeader">   
             <h1 className="Title">Product Detail</h1>
             <Box>
              <Link to='/'>Home</Link>

              <Link to='#'>Product Detail</Link>
             </Box>
          </Grid>
          <Grid item xs={12} sm={12} md={6} style={{display:'flex',justifyContent:"center",alignItems:'center'}}>  
            <Box className="ProductDetailImage">
              <img src={oneproduct.image}/>
            </Box>
          </Grid>
          <Grid item xs={12} sm={12} md={6} className="ProductDetailInfo">   
            <div>PRODUCT DETAIL</div>
               {oneproduct.length === 0 ? (
              <h1>is loading ....</h1>
            ) : (    
            <div>
                <Button style={{marginBottom:"20px"}} onClick={()=>  navigate("/")}  variant="contained" color="primary">GO BACK</Button>
                <div>
                    <h1>title : </h1>{oneproduct.title}
                    <h1>price : </h1>{oneproduct.price}
                    <h1>category : </h1>{oneproduct.category}
                </div>
            </div> 
            )}
          </Grid>
        </Grid>
)}

export default ProductDetail
