import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Grid,Button, Typography,Box,Container} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  backgroundFix: {
    height: '300px',
    width: '100%',
    backgroundSize: 'cover',
    backgroundAttachment: 'fixed',
    backgroundColor: 'black',
    zIndex: 2,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  centerTitleContainer: {
    position: 'absolute',
    top: 0,
    right: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'transparent',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  },
  centerTitle: {
    width: 400,
    height: 150,
    backgroundColor: 'transparent',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1,
    '& h1': {
      fontSize: '37px',
      fontWeight: 500,
      color: 'white'
    },
    '& h3': {
      fontSize: '20px',
      fontWeight: 500,
      color: 'white',
      marginTop: 15
    }
  },
  gridContainer: {
    backgroundColor: 'blue',
    display: 'flex',
    alignItems: 'center',
    marginBottom: 50,
    marginTop: 100
  },
  slideContainer: {
    display: 'flex',
    width: '100%',
    padding: 10,
    backgroundColor: 'yellow'
  },
  slideTitle: {
    display: 'flex',
    flexDirection: 'column', marginRight: 20, backgroundColor: 'pink'
  },
  text: {
    fontSize: '16px',
    fontWeight: 400,
    lineHeight: '2rem',
    color: 'blak',
    marginTop: 10,
    fontFamily: 'Open Sans, sans-serif'
  },
  title: {
    fontFamily: 'Open Sans, sans-serif',
    fontSize: '19px',
    fontWeight: 'bold',
    color: '#F78181',
    marginBottom: 10
  },
  list: {
    paddingLeft: 35,
    '& li': {
      fontSize: '16px',
      fontWeight: 400,
      lineHeight: '2rem',
      color: 'blak',
      marginTop: 10,
      fontFamily: 'Open Sans, sans-serif'
    }
  }
}));

const items = [
    {
        id: 1,
        title: '300',
    },
    {
        id: 2,
        title: '400',
    },
    {
        id: 3,
        title: '500',
    }
]
const Terms = () => {
  const classes = useStyles();
  const [countdown, setCountdown] = React.useState(1);

  React.useEffect(() => {
    const timeout = setTimeout(() => {
        if(countdown == 3){
            setCountdown(1);
        }else{
            setCountdown(countdown + 1);
        }
    }, 3000);
    return () => {
        clearTimeout(timeout);
    }
}, [countdown]);

  return (
    <div>
      <Grid container xs={12} className={classes.backgroundFix}>
            <h8>Terms And Conditions</h8>
      </Grid>
      <Container maxWidth="md" style={{ marginBottom: 200 }}>
        <Grid container xs={12} className={classes.gridContainer}>
        <div className={classes.slideContainer}>
          <div className={classes.slideTitle}> 
            {items.map((index,i)=>{
              return(
                <div style={{backgroundColor: index.id === countdown? 'lightgray' : 'red', transition: 'all 1s ease-out'}} >
                  <h1 style={{ marginRight: 10}}>{index.id}</h1>
                </div>
              )
            })}
          </div>
          <div style={{display: 'flex', flexDirection: 'column', backgroundColor: 'pink'}} > 
            {items.map((index,i)=>{
              return(
                <>
                {index.id === countdown && (
                  <div style={{backgroundColor: 'lightgray', transition: 'all 1s ease-out'}} >
                    <h1>{index.title}</h1>
                  </div>
                )}
                </>
              )
            })}
          </div>
        </div>
          <Box style={{ marginBottom: 30 }}>
            <Typography className={classes.title}>Welcome to SmileLand!</Typography>
            <Typography className={classes.text}>
              These terms and conditions outline the rules and regulations for the use of Company
              Name's Website, located at Website.com.
            </Typography>
            <Typography className={classes.text}>
              By accessing this website we assume you accept these terms and conditions. Do not
              continue to use Website Name if you do not agree to take all of the terms and
              conditions stated on this page.
            </Typography>
            <Typography className={classes.text}>
              The following terminology applies to these Terms and Conditions, Privacy Statement and
              Disclaimer Notice and all Agreements: and refers to you, the person log on this
              website and compliant to the Company's terms and conditions. refers to our Company.
              refers to both the Client and ourselves. All terms refer to the offer, acceptance and
              consideration of payment necessary to undertake the process of our assistance to the
              Client in the most appropriate manner for the express.
            </Typography>
          </Box>
          <Box style={{ marginBottom: 30 }}>
            <Typography className={classes.title}>Cookies</Typography>
            <Typography className={classes.text}>
              We employ the use of cookies. By accessing Website Name, you agreed to use cookies in
              agreement with the Company Name's Privacy Policy.
            </Typography>
            <Typography className={classes.text}>
              Most interactive websites use cookies to let us retrieve the user's details for each
              visit. Cookies are used by our website to enable the functionality of certain areas to
              make it easier for people visiting our website. Some of our affiliate/advertising
              partners may also use cookies.
            </Typography>
          </Box>
          <Box style={{ marginBottom: 30 }}>
            <Typography className={classes.title}>License</Typography>
            <Typography className={classes.text}>
              Unless otherwise stated, Company Name and/or its licensors own the intellectual
              property rights for all material on SmileLand. All intellectual property rights are
              reserved. You may access this from SmileLand for your own personal use subjected to
              restrictions set in these terms and conditions.
            </Typography>
            <Typography className={classes.text}>You must not:</Typography>
            <ul className={classes.list}>
              <li>Republish material from SmileLand</li>
              <li>Sell, rent or sub-license material from SmileLand</li>
              <li>Reproduce, duplicate or copy material from SmileLand</li>
              <li>Redistribute content from SmileLand</li>
            </ul>
          </Box>
        </Grid>
      </Container>
    </div>
  );
};

export default Terms;
