import React from 'react'
import { Grid } from '@material-ui/core'
import sh from "../../images/category/sh1.jpg";
import el from "../../images/category/el2.jpg";
import cl from "../../images/category/cl3.jpg";
import { Link } from 'react-router-dom';
import './Category.css';

const categories =[
    {
      name: 'dresses',
      url: 'https://img.freepik.com/free-photo/still-life-spring-wardrobe-switch_23-2150479001.jpg?t=st=1715272465~exp=1715276065~hmac=e1ffb5527a6d886e402c9d3e74b5ff2441e59350eb5ba1dcb0f6c5e69a86fddf&w=360',
      imgUrl: cl
    },
    {
      name: 'shoes',
      url: '/shop/womens-shoes',
      imgUrl: sh
    },
    {
      name: 'jewellery',
      url: '/shop/womens-jewellery',
      imgUrl: el
    }
];

const Category = () => {
  return (
    <Grid id="sh" container className="categorySectionContainer">
      {categories.map((index) => (
        <div className="categoryItem">
          <img src={index.imgUrl}/>
          <div className="overlay">
            <Link to={index.url} style={{textDecoration: 'none'}}>
              <div>
                <h1>{index.name}</h1>
              </div>
            </Link>
          </div>
      </div> 
    ))}
    </Grid>
  )
}

export default Category;
