import React from 'react';
import { Link } from 'react-router-dom';
import { Grid,Button, Typography,Box,Container} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { LocationOn, Style } from '@material-ui/icons';
import Carousel from 'react-material-ui-carousel'
import i from '../images/promotion/chapeau.jpg'
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './Product/slick.css';
import SeeMoreButton from './SeeMoreButton';
import { ArrowRightAlt } from '@material-ui/icons';

const useStyles = makeStyles((theme) => ({
    PromotionContainer: {
        backgroundColor: 'white',
        display: "flex",
        justifyContent:'center', 
        marginTop: 100,  
        //alignItems:'center',
    },
    PromotionItem: {
      //backgroundColor: 'red',

    },
    carousel: {
        width: '90%',
        //height: '100%', 
    },
    containerItem: {
        position: 'relative', 
        display: "flex",
        justifyContent:'center',   
        alignItems:'center', 
    },
    imageItem: {
        minWidth:'250px',
        minHeight:'300px',
        width: '100%',
        height: '500px',
        objectFit:"cover"    
    },
    overlay: {
        bottom: 0,
        margin: '0px auto',   
        position: 'absolute',
        width: '70%',
        height: '20%',
        background : 'rgba(0, 0, 0, 0.1)',
        borderTopLeftRadius: 10,
        borderTopRightRadius: 10,    
        display:"flex",
        justifyContent:"center",
        alignItems:"center",
        animation: "$animWidth .5s forwards"
      },
    priceItem: {
        fontSize: '20px',
        color: 'white',
        textDecoration: 'none'
    },
    containerTitlePromotion: {
      //backgroundColor: 'red',
      padding: 20,
    },
    titlePromotion: {
      color: '#333333',
      textAlign: 'left',
      position: 'relative',
      width: 'max-content',
      fontSize: 25,   
      fontWeight: 400, 
      fontFamily: 'Montserrat, sans-serif',
      letterSpacing: '.05em',
      textTransform: 'uppercase',
      "&:after" :{  
          position: 'absolute',
          content: '""',
          display:"pink",
          left: 0,
          bottom: -15,
          width: "40%",
          height: "3px",
          background: 'brown',
          transition:" 0.3s ease 0s, left 0.3s ease 0s",
        },
    },
    descriptionPromotion: {
        margin: '50px 0px',
        lineHeight: '30px',
        fontSize: '15px',
        color: 'grey',
        fontWeight: 100, 
        fontFamily: 'Montserrat, sans-serif',
        letterSpacing: '0.1em',
        textTransform: 'none',
    },
    buttonItem: {    
        transition: "all 0.3s ease 0s",
        cursor: 'pointer',
        backgroundColor: 'black',
        display: 'block',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '5px 20px',
        textDecoration: 'none',
        '& p': {
            color: 'white',
            fontSize: '8px',
            fontWeight:'bold'
          },
        '&:hover': {
          color: 'white',
          backgroundColor: '#007fbf',
        }
    },
    "@keyframes animWidth": {
        from: { height:'20%' },
        to: { height: '50%' }
    }
}));

const items = [
    {
        price: '300',
        imageUrl: '/static/promotion/pro9.jpg',
    },
    {
        price: '400',
        imageUrl: '/static/promotion/pro10.jpg',
    },
    {
        price: '500',
        imageUrl: '/static/promotion/pro11.jpg',
    },
    {
        price: '600',
        imageUrl: '/static/promotion/pro12.jpg',
    },
    {
        price: '700',
        imageUrl: '/static/promotion/pro4.jpg',
    },
];

const Item = (props) => {
  const classes = useStyles();
  return (
      <div className={classes.containerItem}>
        <img alt="kk" src={props.item.imageUrl} className={classes.imageItem}/>
        <div className={classes.overlay}>
          <h1 className={classes.priceItem}>{props.item.price}</h1>
          <Link to="/shop" className={classes.buttonItem}>
            <p>Shop Now</p>
          </Link>
        </div>
      </div> 
    );
}

export default function PromotionSection() {
  const classes = useStyles();

  const PreviousArrow = ({ onClick }) => (
    <button className="buttonItem2 prev" onClick={onClick}>
      <ArrowRightAlt style={{transform: 'rotate(180deg)'}} />
    </button>
);

  const NextArrow = ({ onClick }) => (
    <button className="buttonItem slick-next" onClick={onClick}>
      <ArrowRightAlt />
    </button>
  );


  return (
    <Grid xs={12} container className={classes.PromotionContainer}>
      <Grid item xs={12} md={6}className={classes.PromotionItem}>
        <Slider 
          autoplay={true}
          dots={false}
          infinite={true}
          speed={2000}
          arrows={true}
          slidesToShow= {1}
          slidesToScroll= {1}
          prevArrow={<PreviousArrow />}
          nextArrow={<NextArrow />}
          className={classes.carousel}
        >
          {
            items.map( (item, i) => <Item key={i} item={item} /> )
          }
          </Slider>
      </Grid>
      <Grid item xs={12} md={6} className={classes.PromotionItem}>
        <Box className={classes.containerTitlePromotion}>
          <h1 className={classes.titlePromotion}>Promotion</h1>
          <Typography className={classes.descriptionPromotion}>
            Finally, you need to provide an implementation of matchMedia to the useMediaQuery
            with the previously guessed characteristics. Using css-mediaquery to emulate matchMedia is recommended.
          </Typography>
          <SeeMoreButton />
        </Box>
      </Grid>
    </Grid>
  );
}

