import React,{useState, useEffect} from 'react';
import { Grid,Button, Typography,Box} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import {useNavigate,useParams,Link} from 'react-router-dom';
import axios from "axios";
import '../../style/ProductDetail.scss';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
        marginBottom: theme.spacing(2),
         width: '100%',
    }
    },
    grid:{
      marginBottom: '20px'
    },
    MenuDetailHeader:{
      display: 'flex',
      alignItems: 'center',
      '& $hr':{
        borderLeft:'1px solid black',
        height: '15px',
      }
    },
    Link:{
      color:'black',
      margin: '10px',
      textDecoration: 'none'
    },
    titleText:{
      fontSize: '30px',
      color: 'black',
    },
    priceText:{
      fontSize: '24px',
      color: '#fe6c61',
      fontWeight:'bold'
    },
    descriptionText:{
      fontSize: '15px',
      color: 'grey',
    },
    ProductDetailColorContainer:{
      marginTop: '20px',
      display: 'flex',
      //padding: '10px',
      alignItems:'center'
    },
    ProductDetailTextId:{
      fontSize: '1rem',
      color: 'black',
      fontWeight:'bold'
    },
    ProductDetailColorItem:{
      cursor: 'pointer',
      display: 'block',
      border: 'none',
      width: '30px',
      height: '30px',
      marginLeft: '15px',
      marginBottom: '5px',
      borderRadius: '50%',
      '&:focus': {
        border: '2px solid #fe6c61',
        color: '#fe6c61'
      }
    },
    ProductDetailSizeItem:{
      cursor: 'pointer',
      display: 'block',
      width: '30px',
      height: '30px',
      marginLeft: '15px',
      marginBottom: '5px',
      '&:hover': {
        border: '1px solid #fe6c61',
        color: '#fe6c61'
      },
      '&:focus': {
        border: '2px solid #fe6c61',
        color: '#fe6c61',
        fontWeight:'bold'
      }
    },
    addCardContainer:{
      marginTop: '25px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      width: '100%',
      ['@media (max-width:780px)']: {
        flexWrap:"wrap",
     }
    },
    plusCard:{
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      backgroundColor: 'white',
      width: '120px',
      height: '45px',
      border: '1px solid grey',
      ['@media (max-width:320px)']: {
        width:"100%",
     }
    },
    plusCardButton:{
      cursor: 'pointer',
      backgroundColor: 'white',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      width: '30px',
      height: '100%',
      color: 'black',
      fontSize: '1.2rem',
      '&:hover': {
        color: 'red'
      },
    },
    plusCardText:{
      fontSize: '1.3rem',
    },
    addCardButton:{
      cursor: 'pointer',
      backgroundColor: 'white',
      display: 'block',
      justifyContent: 'center',
      alignItems: 'center',
      flexGrow: 1,
      marginLeft: '20px',
      height: '45px',
      color: 'black',
      fontSize: '14px',
      fontWeight:'bold',
      border:'none',
      '&:hover': {
        color: 'white',
        backgroundColor: 'black',
      },
     ['@media (max-width:320px)']: {
        width:"100%",
        marginLeft: '0px',
        marginTop: '20px',
     }
    },
    favoritCardButton:{
      cursor: 'pointer',
      backgroundColor: 'white',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      width: '45px',
      height: '45px',
      color: 'black',
      fontSize: '1.2rem',
      border:'none',
      marginLeft: '20px',
      '&:hover': {
        color: 'red'
      },
      ['@media (max-width:780px)']: {
        width:"100%",
        marginLeft: '0px',
        marginTop: '20px',
     }
    },
  }));

const ProductDetail = () => {
    const classes = useStyles();
    let {id} = useParams()
    const [oneproduct,setOneprodut]= useState([])
    const navigate = useNavigate();

  const getSingle = (userid) => {
  axios.get(`https://fakestoreapi.com/products/${userid}`)
  .then((res)=>{
      console.log('data',res.data)
      setOneprodut(res.data)
  })
  .catch((error)=> console.log(error))
 }

useEffect(() => {
    if(id && id !== ""){
      getSingle(id)
    }
 },[id,oneproduct]);

    return (
        <Grid container  className="ProductDetailContainer">
           <Grid item  xs={12} sm={12} className="ProductDetailHeader">   
             <h1 className="Title">Product Detail</h1>
             <Box  className={classes.MenuDetailHeader}>
               <Link to='/' className={classes.Link}>Home</Link>
               <hr/>
               <Link to='#' className={classes.Link}>Product Detail</Link>
             </Box>
          </Grid>
        {oneproduct.length === 0 ? (
              <h1>is loading ....</h1>
            ) : ( 
        <Grid item container xs={12} sm={12} md={10} style={{margin:'100px auto'}}>
          <Grid item xs={12} sm={12} md={6} style={{display:'flex',justifyContent:"center",alignItems:'center'}}>  
            <Box className="ProductDetailImage">
              <img src={oneproduct.image}/>
            </Box>
          </Grid>
          <Grid item xs={12} sm={12} md={6} className="ProductDetailInfo">      
            <Typography className={classes.titleText}>{oneproduct.title}</Typography>
            <Typography className={classes.priceText}>$ {oneproduct.price}</Typography>
            <Typography className={classes.descriptionText}>{oneproduct.description}</Typography>
            <Typography className={classes.descriptionText}>category : {oneproduct.category}</Typography>  
            <Box className={classes.ProductDetailColorContainer}>
              <Typography className={classes.ProductDetailTextId}>Color: </Typography>
              <Box style={{display:'flex',flexWrap:'wrap'}}>
               <button  style={{ backgroundColor: 'blue'}} className={classes.ProductDetailColorItem} />
               <button  style={{ backgroundColor: 'pink'}} className={classes.ProductDetailColorItem} />
               <button  style={{ backgroundColor: 'red'}} className={classes.ProductDetailColorItem} />
               <button  style={{ backgroundColor: 'yellow'}} className={classes.ProductDetailColorItem} />
               <button  style={{ backgroundColor: 'pink'}} className={classes.ProductDetailColorItem} />
               </Box>
            </Box>
            <Box className={classes.ProductDetailColorContainer}>
              <Typography className={classes.ProductDetailTextId}>Size: </Typography>
              <Box style={{display:'flex',flexWrap:'wrap'}}>
               <button className={classes.ProductDetailSizeItem}>
                  L
                </button>
                <button className={classes.ProductDetailSizeItem}>
                  M
                </button>
                <button className={classes.ProductDetailSizeItem}>
                 S
                </button>
               </Box>
            </Box>
            <Box className={classes.addCardContainer}>
              <Box className={classes.plusCard}>
                <Box className={classes.plusCardButton}>-</Box>
                  <Typography className={classes.plusCardText}>1</Typography>
                <Box className={classes.plusCardButton}>+</Box>
              </Box>
               <button className={classes.addCardButton}>ADD TO CARD</button>
               <Box onClick={()=>console.log('aaaaaa')} className={classes.favoritCardButton}> <insta class="fa-brands fa-instagram"></insta></Box>
            </Box>
            <Box className={classes.ProductDetailColorContainer}>
              <Typography className={classes.ProductDetailTextId}>Color: </Typography>
              <Box style={{display:'flex',flexWrap:'wrap'}}>
              <button  style={{ backgroundColor: 'pink'}} className={classes.ProductDetailColorItem} />
               </Box>
            </Box>
          </Grid>
        </Grid>
        )}
        </Grid>
)}

export default ProductDetail
