import React from 'react';
import styles from '../style/c'
import TextField from '@material-ui/core/TextField';
import { Grid,Button,ButtonGroup } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
        marginBottom: theme.spacing(3),
        width: '100%',
    },
    "& .MuiFormLabel-root": {
        color: "#1e1e27", 
        fontSize:"14px"  
    },
    ['@media (max-width:598px)']: {
      marginTop:"30px",
    }

    },
  input: {
      '&:after':{
        borderBottom:"2px solid #1e1e27",
      },   
  },

  }));


const Contact = () => {
  const classes = useStyles();

  return(
  <div id="contact">
     <Grid container >
       <Grid item xs={12} sm={12}>
         <div className={styles.SectionTitle}>
           <h1>Contact me</h1>
 <h3>Don't think you'll send an email with no response... I'm here to help!</h3>

         </div>
        </Grid>
       <Grid container xs={12} sm={12} className={styles.GridContainer}>
         <Grid item xs={12} sm={6} className={styles.GridInfo}>
           <div className={styles.Informations}>
            <div className={styles.envelope}>
               <i class="fas fa-envelope-open"></i>
            </div>
             <div className={styles.Info}>
               <div className={styles.Title}>
                 <i class="fas fa-envelope"></i>
                 <span>Email:</span>
               </div>
               <div className={styles.Description}>
                 ikramferhat2@gmail.com
               </div>
             </div>
             <div className={styles.Info}>
               <div className={styles.Title}>
                 <i class="fas fa-phone-alt"></i>
                 <span>Phone:</span>
               </div>
               <div className={styles.Description}>
                 +213791098242
               </div>
             </div>
             <div className={styles.Icons}>
               <div className={styles.Icon}>
                 <i class="fab fa-github"></i>
                 <i class="fab fa-linkedin"></i>
                 <i class="fab fa-instagram"></i>
               </div>
              </div>
             </div>
         </Grid>     
        <Grid item xs={12} sm={6} className={styles.GridForm}>
          <form className={classes.root}  noValidate autoComplete="off">                  
                    <TextField
                       name="fullname"
                       label="Full Name"
                       type="text"
                       InputProps={{
                        className: classes.input,
                       }}
                       />
                    <TextField
                       name="email"
                       label="Email"
                       type="email"
                       InputProps={{
                        className: classes.input,
                       }}
                       />
                    <TextField
                       label="Type your Message"
                       multiline
                       rows={4}
                       maxRows={4}
                       InputProps={{
                        className: classes.input,
                       }}
                       />                   
                    <button   
                        type='submit'
                        style={{marginTop:"10px"}}
                        variant="contained"
                        className={styles.button}
                        >SEND
                    </button>
            </form>   
          </Grid>
       </Grid>  
     </Grid>  
  </div>
  )
}

export default Contact;
