import React, {useState,useEffect}from 'react';
import Toolbar from '@material-ui/core/Toolbar';
import { makeStyles } from '@material-ui/core/styles';
import { AppBar } from '@material-ui/core';
import {Link} from "react-router-dom"
import {List, ListItemText, Box, useScrollTrigger, Typography} from "@material-ui/core";
import ShoppingCartOutlinedIcon from '@material-ui/icons/ShoppingCartOutlined';
import { Badge} from '@material-ui/core';
import { HeaderBottomMenu, HeaderTopMenu } from '../DATA';
import SubjectIcon from '@material-ui/icons/Subject';
import { Search, Twitter, Instagram, Facebook} from '@material-ui/icons';
import CloseIcon from '@material-ui/icons/Close';
import { Delete } from '@material-ui/icons';
import { NavLink, useLocation, useNavigate } from 'react-router-dom';
import { useCart } from 'react-use-cart';
import img1 from '../images/promotion/pro1.jpg';
import img2 from '../images/promotion/pro2.jpg';
import axios from "axios"
import Product from './Product/Product';

const useStyles = makeStyles((theme) => ({
  Toolbar: {  
    display: 'flex',
    justifyContent: 'space-between',
    flexDirection: 'column',
    alignItems: 'center',
    padding: "0px 5%",
    ['@media (max-width:320px)']: {        
      padding: "0px 5%",
    },
    ['@media (max-width:780px)']: { 
      flexDirection: 'row',       
      justifyContent: 'space-between',
    }
  },
  Toolbar2: {  
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: "0px 5%",
    borderBottom: '1px solid rgba(255, 255, 255, 0.2)',
  },
  logoContainer: {  
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: "30px",
    width: '100%',
    padding: "5px 0",
    margin: '10px',
    ['@media (max-width:780px)']: {
      display: 'none'
    }
  },
  Logo: {
      fontSize: "35px",
      fontWeight: 400,
      textAlign: 'center',
      fontFamily: 'Italianno, cursive',
      ['@media (max-width:780px)']: {
        display: 'none'
      }
  },
  LogoMobile: {
    display: "none",
    fontSize: "30px",
    fontWeight: 400,
    textAlign: 'center',
    fontFamily: 'Italianno, cursive',
    marginLeft: 10,
    ['@media (max-width:780px)']: {
      display: 'block'
    }
  },
  List: {
      display: 'flex',
      //flex: '1 0 0',
      alignItems: 'center',
      listStyle: "none",
      alignItems: 'center',
      margin: '0px',
      ['@media (max-width:780px)']: {        
          display: "none",
        }
  },
  ListMobile: {
      display: "none",
      transition: "display 3s ease-in",
      zIndex: 2,
      ['@media (max-width:780px)']: {
          position:"absolute",
          display: "block",
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'space-around',
          alignItems: 'center',
          backgroundColor: "lightgray",
          color:"black",
          right: "0px",
          top: "50px",
          width: "100%",
          paddind: 10,
          textAlign: "center"
   } },
  mhContainer: {
    display: 'none',
    alignItems: 'center',
    ['@media (max-width:780px)']: {
      display: 'flex'
    }
  },
  li:{
    position: 'relative',
    margin: '0 0px',
    '&:hover':{
      '& $dropdown':{
        display: "block",
        visibility: 'visible',
        opacity: 1
      },
    },
  },
  dropdown: {
    position: 'absolute',
    display: 'block',
    opacity: 0,
    top: '100%',
    minWidth: '100px',
    width: 'max-content',
    textAlign: 'left',
    visibility: 'hidden',
    transition: "all .3s ease-in",
    zIndex: 3,
    border: '1px solid lightgray',
    borderTop: 'none',
    borderBottom: 'none',
    '& li': {
      width: '100%',
      borderBottom: '1px solid pink',
      '& a': {
        display: 'block', 
        padding: '20px 10px',
        fontSize: "12px",
        fontWeight: 400,
        color: 'grey',
        transition: "all .3s ease-in",
        cursor: "pointer",
        textDecoration: "none",
        fontFamily: 'Montserrat, sans-serif',
        letterSpacing: '.2em',
        textTransform: 'uppercase',
        "&:hover":{ 
          backgroundColor: 'lightgray',
          color: 'black'
        },
    },
    },
   },
  links: {
      position: 'relative',
      display: 'block', 
      fontSize: "12px",
      fontWeight: 400,
      transition: "all 0.5s ease-out",
      cursor: "pointer",
      textDecoration: "none",
      fontFamily: 'Montserrat, sans-serif',
      letterSpacing: '.2em',
      textTransform: 'uppercase',
      "&:after" :{  
        position: 'absolute',
        content: '""',
        display:"pink",
        left: 0,
        bottom: 0,
        width: "0px",
        height: "3px",
        background: 'red',
        transition:" 0.3s ease 0s, left 0.3s ease 0s",
      },
      "&:hover:after":{ 
        width: "100%",
        left: "0",

      },
  },
  colorTextLight: {
    color: 'black',
    "&:hover":{
      color: "green",
    },
    ['@media (max-width:780px)']: {
      color: "black",
    }
  },
  colorTextDark: {
    color:  'white',
    "&:hover":{
      color: "green",
    },
    ['@media (max-width:780px)']: {
      color: "black",
    }
  },
  IconContainer: {     
    cursor: "pointer",
  },
  Icon: { 
    cursor: "pointer",
    marginRight: 15,
    fontSize: 18,    
    color: "black",
    transition: "all 0.5s ease-out",
    "&:hover":{
      color: "green",
    },
  },
  mobileicon: {
      display: "none",
      transition: "0.5s ease-in",
      ['@media (max-width:780px)']: {
          display:" block",
          color: "black",
       }
  },
  showMenu: {
    backgroundColor: 'red',
    transform: 'translateX(100%)',

  },
  sidebarOverlay: {
    position: 'fixed',
    overflowX: 'hidden',
    top: 0,
    right: 0,
    height: '100%',
    width: '100%',
    zIndex: 1,
    pointerEvents: 'auto',
    visibility: 'hidden',
    backgroundColor: '#000',
    opacity: '0.75',
    '&::-webkit-scrollbar': {
      display: 'none',
      width:0,

    }
  },
  sidebar: {
    position: 'fixed',
    display: 'flex',
    flexDirection: 'column',
    overflowY: 'auto',
    overflowX: 'hidden',
    top: 0,
    backgroundColor: 'white',
    right: 0,
    height: '100%',
    width: '450px',
    //transform: 'translateX(0%)',
    zIndex: 2,
    transition: 'all 3s ease-in',
    '@media (max-width:768px)': {
      width: '100%'
    }
  },
  sidebarHeader: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 10,
    backgroundColor: "white",
    '& h3': {
      fontSize: "18px",
      fontWeight: 400,
      fontFamily: 'Montserrat, sans-serif',
      letterSpacing: '.2em',
      textTransform: 'uppercase',
      color: 'black'
    },
    '& button': {
      border: 'none',
      backgroundColor: 'transparent',
      padding: 10,
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center'    
    }
  },
  cartSidContainer: {
    display: 'flex',
    overflowY: 'auto',
    overflowX: 'hidden',
    maxHeight: '40vh',
    flexDirection: 'column',
    //backgroundColor: "black",
    padding: 10,
    flexGrow: 1

  },
  cart: {
    height: 100,
    backgroundColor: "white",
    boxShadow: '0 0 16px rgb(0 0 0 / 15%)',
    display: 'flex',
    padding: 10,
    marginTop: 15,
  },
  containerImage: {
    width: '100px',
    height: '100px',
    overflow: 'hidden',
    backgroundColor: "white",
  },
  image: {
    height: "100%",
    minWidth: '100%',
    objectFit: 'contain',
  },
  discreption: {
    display: 'flex',
    flexDirection: 'column',
    flex: 1,
    height: 'max-content',
    margin: '0px 5px',
    '& h2': {
      fontSize: "15px",
      fontWeight: 500,
      fontFamily: 'Montserrat, sans-serif',
      letterSpacing: '.1em',
      textTransform: 'none',
      color: 'black',
      margin: 0
    },
    '& h4': {
      fontSize: "15px",
      fontWeight: 400,
      fontFamily: 'Montserrat, sans-serif',
      letterSpacing: '.1em',
      textTransform: 'none',
      color: 'grey',
      margin: 0,
      marginTop: 10
    }
  },
  delete: { 
    fontSize: 20,    
    color: "grey",
    cursor: 'pointer'
    },
  cartTotal: {
    width: '100%',
    boxSizing: 'border-box',
    backgroundColor: "lightgray",
    padding: 20,
    paddingBottom: 20,
    marginTop: 15
  },
  cartTotalItem: {
    marginBottom: 10,
    width: '100%',
    height: '30px',
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    backgroundColor: 'white',
    borderBottom: '1px solid grey',
  },
  cartTotalItemTextT: {
    fontSize: "12px",
    fontWeight: 400,
    fontFamily: 'Montserrat, sans-serif',
    letterSpacing: '.1em',
    textTransform: 'uppercase',
    color: 'black',
  },
  cartTotalItemTextD: {
    fontSize: '12px',
    fontWeight: 400,
    fontFamily: 'Montserrat, sans-serif',
    letterSpacing: '.1em',
    textTransform: 'none',
    color: 'grey'
  },
  addCardButton:{
    marginTop: 20,
    cursor: 'pointer',
    backgroundColor: 'black',
    display: 'block',
    justifyContent: 'center',
    alignItems: 'center',
    padding: 10,
    width: '100%',
    fontFamily: 'Montserrat, sans-serif',
    letterSpacing: '.1em',
    color: 'white',
    fontSize: '12px',
    textTransform: 'uppercase',
    border: '2px solid transparent',
    '&:hover': {
      color: 'white',
      backgroundColor: '#007fbf',
    }
  },
  searchBar: {
    position: 'relative',
    transition: "all 3s ease",
    WebkitTransition: 'all 3s ease',
    mozTransition: 'all 3s ease',
    width: '90%',
    margin: '0 auto',
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'lightgray',
    padding: 5,
    '& span': {   
      cursor: "pointer",
      height: '100%',   
      marginLeft: 10
      },
  },
  closeSearch: {
    fontSize: 20,
    color: 'black',
    transition: "all 0.5s ease-out",
    "&:hover":{
      color: "red",
    },
  },
  inputSearch: {
    display: 'block',
    width: '90%',
    height: '30px',
    color: 'black',
    borderRadius: 0,
    border: 'none',
    backgroundColor: 'transparent',
    border: '1px solid white',
    '&:focus': {
      outline: 'none',
    }
  },
  resultContainer: {
    top: '100%',
    left: 0,
    position: 'absolute',
    width: '100%',
    height: '300px',
    display: "flex",
    backgroundColor: 'white',
    overflowY: 'scroll',
  },
  filtredProducts: {
    display: 'flex',
    flexDirection: 'column',
  }
  }))


const Header = (props) => {
    const classes = useStyles();
    const [isMobile,setIsMobile] = useState(false);
    const [navBg, setNavBg] = useState(false);
    const [navDrop, setNavDrop] = useState(false);
    const [colorT, setColorT] = useState(false);
    const [navbarOpen, setNavbarOpen] = useState(false);
    const [searchOpen, setSearchOpen] = useState(false);
    const [searchField, setSearchField] = useState("");
    const [products,setProducts] = useState([])
