import React,{useState, useEffect} from 'react';
import { Grid,Button, Typography,Box} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import {useNavigate,useParams,Link} from 'react-router-dom';
import axios from "axios";
import Product from '../components/Product/Product';
import Pagination from '../components/Pagination'

const useStyles = makeStyles((theme) => ({
    ProductDetailContainer: {
      backgroundColor: 'rgb(255, 255, 255)'
    },
    ProductDetailHeader: {
      backgroundColor: 'rgb(218, 209, 209)',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      height: '130px'
    },
    Title: {
      fontWeight: 'bold',
      color: 'black',
      fontSize: '2rem',
      padding: '0px',
      margin: '0px',
      marginSottom: '12px'
    },
    grid:{
      marginBottom: '20px'
    },
    MenuDetailHeader:{
      display: 'flex',
      alignItems: 'center',
      '& $hr':{
        borderLeft:'1px solid black',
        height: '15px',
      }
    },
    Link:{
      color:'black',
      margin: '10px',
      textDecoration: 'none'
    },
    titleText:{
      fontSize: '30px',
      color: 'black',
    },
    priceText:{
      fontSize: '24px',
      color: '#fe6c61',
      fontWeight:'bold'
    },
    descriptionText:{
      fontSize: '15px',
      color: 'grey',
    },
    ProductDetailColorContainer:{
      marginTop: '20px',
      display: 'flex',
      //padding: '10px',
      alignItems:'center',
      '& $a':{
          cursor: 'pointer',
          display: 'flex',
          alignItems:'center',
          justifyContent:'center',
          backgroundColor:'white',
          textDecoration:'none',
          color: 'black',
          width: '20px',
          height: '20px',
          marginLeft: '10px',
          borderRadius: '50%',
          border: '1px solid white',
          fontSize: '15px',
          '&:hover': {
            border: '1px solid black',
            color: 'white',
            backgroundColor: 'black'
          }
        },
    },
    ProductDetailTextId:{
      fontSize: '1rem',
      color: 'black',
      fontWeight:'bold'
    },
    ProductDetailColorItem:{
      cursor: 'pointer',
      display: 'block',
      border: 'none',
      width: '30px',
      height: '30px',
      marginLeft: '15px',
      marginBottom: '5px',
      borderRadius: '50%',
      '&:focus': {
        border: '2px solid #fe6c61',
        color: '#fe6c61'
      }
    },
    ProductDetailSizeItem:{
      cursor: 'pointer',
      display: 'block',
      width: '30px',
      height: '30px',
      marginLeft: '15px',
      marginBottom: '5px',
      '&:hover': {
        border: '1px solid #fe6c61',
        color: '#fe6c61'
      },
      '&:focus': {
        border: '2px solid #fe6c61',
        color: '#fe6c61',
        fontWeight:'bold'
      }
    },
    addCardContainer:{
      marginTop: '25px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      width: '100%',
      ['@media (max-width:780px)']: {
        flexWrap:"wrap",
     }
    },
    plusCard:{
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      backgroundColor: 'white',
      width: '120px',
      height: '45px',
      border: '1px solid lightgrey',
      ['@media (max-width:320px)']: {
        width:"100%",
     }
    },
    plusCardButton:{
      cursor: 'pointer',
      backgroundColor: 'white',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      width: '30px',
      height: '100%',
      color: 'black',
      fontSize: '1.2rem',
      '&:hover': {
        color: 'red'
      },
    },
    plusCardText:{
      fontSize: '1.3rem',
    },
    addCardButton:{
      cursor: 'pointer',
      backgroundColor: 'black',
      display: 'block',
      justifyContent: 'center',
      alignItems: 'center',
      flexGrow: 1,
      marginLeft: '20px',
      height: '45px',
      color: 'white',
      fontSize: '14px',
      fontWeight:'bold',
      border:'none',
      '&:hover': {
        color: 'white',
        backgroundColor: 'black',
      },
     ['@media (max-width:320px)']: {
        width:"100%",
        marginLeft: '0px',
        marginTop: '20px',
     }
    },
    favoritCardButton:{
      cursor: 'pointer',
      backgroundColor: 'pink',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      width: '45px',
      height: '45px',
      color: 'black',
      fontSize: '1.2rem',
      border:'none',
      marginLeft: '20px',
      '&:hover': {
        color: 'white'
      },
      ['@media (max-width:780px)']: {
        width:"100%",
        marginLeft: '0px',
        marginTop: '20px',
     }
    },
    ProductDetailTextShare:{
      fontSize: '14px',
      color: 'grey',
      fontWeight:200
    },
    gridContainer: {   
      padding: "0px 1%",
      display: "flex",
      justifyContent: "center",
    },
    gridFilter: {   
    padding: "0px 10px",
    display: "flex",
    justifyContent: "center",
    backgroundColor:'red'
    },
  }));

const Shop = () => {
    const classes = useStyles();
    const [products,setProducts] = useState([])
    const [currentPage, setCurrentPage] = useState(1);
    const [postsPerPage] = useState(10);

     // Get current posts
  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = products.slice(indexOfFirstPost, indexOfLastPost);

  // Change page
  const paginate = pageNumber => setCurrentPage(pageNumber);

    const showProducts = () => {
        axios.get("https://fakestoreapi.com/products/")
        .then((response)=>{
          console.log('product',response.data)
          setProducts(response.data)
        })
        .catch((err)=>{
          console.log("err", err)
      })
    }

useEffect(() => {
    showProducts();
  }, []);


    return (
        <Grid container  className={classes.ProductDetailContainer}>
           <Grid item  xs={12} sm={12}  className={classes.ProductDetailHeader}>   
             <h1  className={classes.Title}>Shop</h1>
             <Box  className={classes.MenuDetailHeader}>
               <Link to='/' className={classes.Link}>Home</Link>
               <hr/>
               <Link to='#' className={classes.Link}>Shop</Link>
             </Box>
          </Grid>
          <Grid container style={{display:'flex',justifyContent:'space-between',marginTop:'50px'}}>
           <Grid item  xs={12} md={2} className={classes.gridFilter} > 
           <h4  className={classes.Title}>Category</h4>
           </Grid>  
          <Grid container item  xs={12} md={10} className={classes.gridContainer}>
          {
          currentPosts.map((index,i)=>{
            if(index.category)
              {
                return(
                  <Product 
                    id={index.id} 
                    title={index.title} 
                    image={index.image} 
                    price={index.price}  
                    category={index.category}
                  />
                    )     
                }
             })
            }
         </Grid>
         </Grid>
         <Pagination
         paginate={paginate}
         totalPosts={products.length}
         postsPerPage={postsPerPage}
        />
        </Grid>
)}

export default Shop;
