import w from "../../images/category/women11.jpg";
import { Link } from 'react-router-dom';
import './Category.css';

const useStyles = makeStyles((theme) => ({
    overlayl: {
        position: 'relative', 
        width:'250px',
        height:'200px',
        backgroundColor: 'blue',
        display: "flex",
        justifyContent:'center',   
        alignItems:'center',
        ['@media (max-width:900px)']: {
            width:"150px",
            height:"100px",
         }
    },
    image: {
        width: '100%',
        height: "100%",
        objectFit:"cover",     
    },
    overlay: {
        top: 0,
        left: 0,
        position: 'absolute',
        width: '100%',
        height: '100%',
        background : 'rgba(0, 0, 0, 0.2)',    
        display:"flex",
        justifyContent:"center",
        alignItems:"center"
      },
    center : {
        background: "rgba(0, 0, 0, 0.2)",
        color:theme.palette.fontColor.primary,
        width:"100px",
        height:"50px",
        display:"flex",
        justifyContent:"center",
        alignItems:"center",
        padding: 10,
        textDecoration: 'none'
    },
    name: {
        fontSize: '20px',
        color: 'white',
        textDecoration: 'none'
    },
    button: {    
        transition: "all 0.3s ease 0s",
        color:theme.palette.fontColor.primary,
        position: "relative",
        cursor: "pointer",
        "&:after" :{  
        position: 'absolute',
        content: '""',
        display:"block",
        left: 0,
        top: "20px",
        right: "50%",
        bottom: "0",
        width: "0px",
        height: "2px",
        background: theme.palette.fontColor.primary,
        transition:" 0.3s ease 0s, left 0.3s ease 0s",
         },
        "&:hover:after":{ 
                width: "50%",
                left: "0"
            },
        },

}))

const categories =[
    {
      name: 'clothes',
      url: '/shop/clothes'
    },
    {
      name: 'shoes',
      url: '/shop/shoes'
    },
    {
      name: 'electronics',
      url: '/shop/electronics'
    }
];

const Category = () => {
  const classes = useStyles();
  return (
    <Grid id="sh" container className="categorySectionContainer">
      {categories.map((index) => (
        <div className="categoryItem">
          <img src={m}  className={classes.image}/>
          <div className={classes.overlay}>
            <Link to={index.url} style={{textDecoration: 'none'}}>
              <div className={classes.center}>
                <h1 className={classes.name}>{index.name}</h1>
              </div>
            </Link>
          </div>
      </div> 
    ))}
    </Grid>
  )
}

