import React,{useState, useEffect, useContext, useRef} from 'react';
import { Grid,Button, Typography,Box,Container} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import {useNavigate,useParams,Link} from 'react-router-dom';
import axios from "axios";
import paypal from '../../images/creditCard/paypal22.png';
import viza from '../../images/creditCard/visa-credit-card.png';
import skrill from '../../images/creditCard/skrill.png';
import mastercard from '../../images/creditCard/mastercard11.png';
import Close from '@material-ui/icons/Close';
import { useCart } from 'react-use-cart';
import AuthContext from '../../context/AuthContext';
import HeaderTitle from '../../components/HeaderTitle/HeaderTitle';
import './CartPage.css';

const useStyles = makeStyles((theme) => ({

    ProductDetailHeader: {
      backgroundColor: 'rgb(218, 209, 209)',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      height: '130px'
    },
    Title: {
      fontWeight: 'bold',
      color: 'black',
      fontSize: '2rem',
      padding: '0px',
      margin: '0px',
      marginSottom: '12px'
    },
    grid:{
      marginBottom: '20px'
    },
    MenuDetailHeader:{
      display: 'flex',
      alignItems: 'center',
      '& $hr':{
        borderLeft:'1px solid black',
        height: '15px',
      }
    },
    Link:{
      color:'black',
      margin: '10px',
      textDecoration: 'none'
    },
    container: {
      backgroundColor:'white',
      marginTop: '50px',
      paddingBottom: '100px'
    },
    gridContainer: {   
      height: '100%',
      display: "flex",
      alignItems: 'flex-start'
    },
    HeaderTable: {   
      backgroundColor:'#fff',
      borderBottom: '1px solid lightgrey',
      position: 'relative',
      '&:hover': {
        backgroundColor: '#f4f4f4',
        '& $RemoveIcon': {
          display: 'block'
        }
      },
      ['@media (max-width: 599px)']: {
      }
    },
    HeaderItem: {   
      padding:10,
