import React from 'react'
import { Grid } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles';
import img from "../images/blog/blog8.jpg";
import Button from '@material-ui/core/Button';
import KeyboardDoubleArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import '../style/Blog.scss';

const useStyles = makeStyles((theme) => ({
    GridContainer: {
        position: 'relative',  
        height:'50rem'
    },
    GridButton: {
        position: 'absolute',
        top: '72%',
        width: '100%',
        display:'flex',
        justifyContent:'center'
    },
    Button : {
        display: "flex",       
        flexDirection: 'column',
        borderRadius: '100%',
        width:'80px',
        height:'80px',
        backgroundColor:'black',
        color:'white'
    },
    banner: {
        clipPath: "polygon(100% 0, 100% 50%, 50% 70%, 0 50%, 0 0)",
        width: "100%",
        height: "100%",
        objectFit:"cover",
        backgroundPosition: 'right'

    },
    overlay: {
        top: 0,
        left: 0,
        position: 'absolute',
        width: '100%',
        height: '100%',
        display: "flex",
        justifyContent:"flex-start",
        alignItems:"center",
      },
    content: {
        marginBottom:'150px',
        height:"400px",
        padding: "0 5%",
        color: theme.palette.fontColor.primary,
        ['@media (max-width:780px)']: {        
            height:"200px",
          },
        ['@media (max-width:400px)']: {        
            height:"150px",
          }
    },
    contentText :{
        fontSize: "50px",
        backgroundColor:'white',
        textTransform: "uppercase",
        ['@media (max-width:780px)']: {        
            fontSize: "30px",
          },
        ['@media (max-width:400px)']: {        
              fontSize: "25px",
            }
    },
    contentText1 :{
        fontSize: "20px",
        textTransform: "none",
        marginBottom: "30px",
        ['@media (max-width:780px)']: {        
            fontSize: "15px",
            marginBottom: "20px",
          },
        ['@media (max-width:400px)']: {        
            fontSize: "12px",
            marginBottom: "10px",
          }
    },
}))
const Blog = () => {
    const classes = useStyles();

    return (
        <div id="contact">
          <Grid container > 
            <Grid item xs={12} sm={12} className="BlogHeroContainer">
              <img src={img} className="HeroImage"/>
              <div className='Overlay'>
                <h1>Our Blog</h1>
              </div>
            </Grid>
            <Grid container item xs={12} sm={12} className="BlogHeroContainer">
              <Grid item xs={12} sm={4} className="BlogHeroContainer">
              </Grid>
              <Grid item xs={12} sm={4} className="BlogHeroContainer">
              </Grid>
              <Grid item xs={12} sm={12} className="BlogHeroContainer">
              </Grid>
            </Grid>
            </Grid>
       </div>
    )
}

export default Blog
