import React from 'react'
import {Link} from "react-router-dom"
import { makeStyles } from '@material-ui/core/styles';
import { Grid, Typography, Box } from '@material-ui/core';
import Rating from '@mui/material/Rating';
import { useCart } from 'react-use-cart';
import {useNavigate} from 'react-router-dom';
import { RemoveRedEyeOutlined } from '@material-ui/icons';
import './product.css';

const useStyles = makeStyles((theme) => ({
    GridItem: {    
        backgroundColor: 'white',
        padding: '20px',
        width: "200px", //** */
        height: "200px",//** */
        position: 'relative',
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        margin: 10,
        '&:hover': {
          '& $center': {
            opacity: 1
          }
        }
    },
    image: {
        height: '100%',
        width: '100%',
        objectFit: 'contain',
        transition: 'all 2s',

    },
    top : {
        width:"100%",
        boxSizing: 'border-box',
        display:"flex",
        alignItems:"center",
    },
    text: {
        fontFamily: 'Raleway, sans-serif',
        fontSize: '14px',
        color: 'white',
    },
    button: {
        transition: "all 0.3s ease",
        cursor: 'pointer',
        backgroundColor: 'black',
        display: 'block',
        justifyContent: 'center',
        alignItems: 'center',
        padding: 10,
        width: '100%',
        fontFamily: 'Montserrat, sans-serif',
        letterSpacing: '.1em',
        color: 'white',
        fontSize: '12px',
        textTransform: 'uppercase',
        outline: 'none',
        border: 'none',
        '&:hover': {
          color: 'white',
          backgroundColor: '#007fbf',
        }
        },
      viewIcon: {
        position: 'absolute',
        bottom: '50%',
        right: 10,
        transition: "all 0.5s ease",
        cursor: 'pointer',
        color: 'white',
        '&:hover': {
          color: 'black',
        }
      }

}))

const Product = (props) => {
    const navigate = useNavigate();
    const classes = useStyles();
    const { addItem } = useCart();
    return (
      <div className="productContainer">   
        <img  src={props.image}  className={classes.image} />
        <div className="overlay" />
        <div className="center">
          <RemoveRedEyeOutlined onClick={()=> {navigate(`/detail/${props.id}`)}}  className={classes.viewIcon} />
          <div className={classes.top}>
            <Rating 
              style={{ flex: 1, fontSize: 14}}
              name="half-rating" defaultValue={props.rating.rate} precision={0.5} readOnly
            />
            <span className={classes.text}>${props.price}</span>
          </div>
          <button className={classes.button} onClick={()=>addItem(props.item)}>
            add to cart
          </button>
        </div>
      </div>
    )
}

export default Product

